}

.detailActiveSession .asbContent {
  padding: 14px 18px 14px 20px;
  gap: 6px;
}

.detailActiveSession .asbTitle {
  font-size: 18px;
}

.detailActiveSession .asbClock {
  font-size: 36px;
}

.detailActiveSession .asbXp {
  font-size: 18px;
}

.detailActiveSession .asbOpenBtn,
.detailActiveSession .asbStopBtn {
  height: 36px;
}
.detailSegmentBtn {
  margin-top: 8px;
  align-self: flex-start;
}

.detailChips {
  display: flex;
  flex-wrap: wrap;
  gap: 6px;
  align-items: center;
  padding: 6px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel2);
}

.detailChip {
  height: 24px;
  border-radius: 999px;
  padding: 0 8px;
  font-size: 11px;
  font-weight: 800;
  display: inline-flex;
  align-items: center;
  gap: 6px;
  border: 1px solid var(--accentMid);
  background: var(--accentSoft);
  color: var(--text);
}

.detailChip.active {
  border-color: var(--accentBorder);
  background: var(--accentMid);
}

.detailChipRemove {
  font-weight: 900;
  color: var(--muted);
}

.detailChipInput {
  flex: 1;
  min-width: 120px;
  border: none;
  background: transparent;
  outline: none;
  font-size: 12px;
  color: var(--text);
}

:root[data-theme='dark'] .detailInlineBtn {
  border-color: rgba(148, 163, 184, 0.08);
  background: rgba(148, 163, 184, 0.06);
}

:root[data-theme='dark'] .detailChip {
  border-color: var(--accentBorder);
  background: var(--accentMid);
}

.charGrid {
  display: grid;
  grid-template-columns: repeat(4, minmax(0, 1fr));
  gap: 8px;
}

.charChip {
  height: 32px;
  border-radius: 999px;
  border: 1px solid rgba(17, 24, 39, 0.12);
  background: rgba(17, 24, 39, 0.03);
  color: var(--text);
  font-weight: 950;
  letter-spacing: 0.4px;
