  resize: vertical;
  font: inherit;
  line-height: 1.4;
}

.mdEditor {
  border-radius: 12px;
  border: 1px solid var(--border);
  background: var(--panel2);
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

.mdEditorTop {
  padding: 8px;
  border-bottom: 1px solid rgba(17, 24, 39, 0.08);
  display: flex;
  justify-content: flex-end;
}

.mdEditorTabs {
  display: inline-flex;
  gap: 6px;
}

.mdTab {
  height: 30px;
  padding: 0 10px;
  border-radius: 10px;
  border: 1px solid rgba(17, 24, 39, 0.10);
  background: rgba(17, 24, 39, 0.04);
  font-weight: 900;
  color: var(--text);
  cursor: pointer;
}

.mdTab.active {
  border-color: var(--accentBorder);
  background: var(--accentSoft);
}

.mdTextarea {
  flex: 1;
  min-height: 140px;
  width: 100%;
  border: none;
  outline: none;
  padding: 12px;
  background: transparent;
  color: var(--text);
  resize: vertical;
  font: inherit;
  line-height: 1.55;
}

.mdPreviewPane {
  flex: 1;
  min-height: 140px;
  padding: 8px;
  overflow: auto;
