}

.mdTaskStart.running {
  border-color: var(--accentBorder);
  background: var(--accentSoft);
  color: var(--accent);
  box-shadow: 0 0 0 2px rgba(251, 146, 60, 0.2);
}

.mdTaskStart.running.over {
  border-color: rgba(185, 28, 28, 0.4);
  background: rgba(254, 226, 226, 0.8);
  color: #b91c1c;
}

.mdCheck {
  border: 0;
  background: transparent;
  padding: 0;
  cursor: pointer;
}

.mdCheck:disabled {
  cursor: default;
  opacity: 0.5;
}

.mdCheckBox {
  width: 16px;
  height: 16px;
  border-radius: 4px;
  border: 1px solid rgba(17, 24, 39, 0.2);
  background: rgba(255, 255, 255, 0.7);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  position: relative;
}

.mdCheck.checked .mdCheckBox {
  background: var(--accentSoft);
  border-color: var(--accentBorder);
}

.mdCheck.checked .mdCheckBox::after {
  content: '';
  width: 6px;
  height: 10px;
  border-right: 2px solid var(--accent);
  border-bottom: 2px solid var(--accent);
  transform: rotate(45deg);
}

.mdChip {
  display: inline-flex;
  align-items: center;
  gap: 4px;
  font-size: 11px;
  font-weight: 900;
  color: rgba(17, 24, 39, 0.72);
  background: rgba(255, 255, 255, 0.75);
  border: 1px solid rgba(17, 24, 39, 0.12);
  border-radius: 999px;
  padding: 0 6px;
  margin: 0 4px 2px 0;
  white-space: nowrap;
}

.mdChip-tag {
  background: rgba(234, 179, 8, 0.12);
  border-color: rgba(234, 179, 8, 0.22);
  color: rgba(161, 98, 7, 0.85);
}

.mdChip-person {
  background: rgba(59, 130, 246, 0.10);
  border-color: rgba(59, 130, 246, 0.22);
  color: rgba(30, 64, 175, 0.85);
}

.mdChip-ctx {
  background: rgba(16, 185, 129, 0.12);
  border-color: rgba(16, 185, 129, 0.22);
  color: rgba(6, 95, 70, 0.85);
}

.mdChip-loc {
  background: rgba(244, 114, 182, 0.12);
  border-color: rgba(244, 114, 182, 0.22);
  color: rgba(190, 24, 93, 0.85);
}

.mdChip-goal,
.mdChip-project,
.mdChip-skill {
  background: rgba(99, 102, 241, 0.12);
  border-color: rgba(99, 102, 241, 0.24);
  color: rgba(67, 56, 202, 0.9);
}

.md code {
  font-family: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, 'Liberation Mono', 'Courier New', monospace;
  font-size: 12.5px;
  padding: 2px 6px;
  border-radius: 8px;
  background: rgba(17, 24, 39, 0.06);
  border: 1px solid rgba(17, 24, 39, 0.10);
}

.md pre {
  padding: 12px;
  border-radius: 12px;
  background: rgba(17, 24, 39, 0.06);
  border: 1px solid rgba(17, 24, 39, 0.10);
  overflow: auto;
}

.md pre code {
  padding: 0;
  border: none;
  background: transparent;
}

.md blockquote {
  margin: 10px 0;
  padding: 8px 12px;
  border-left: 4px solid var(--accentBorder);
  background: var(--accentSoft);
  border-radius: 10px;
}

.md table {
  width: 100%;
  border-collapse: collapse;
  margin: 10px 0;
  font-size: 13px;
}

.md th,
.md td {
  border: 1px solid rgba(17, 24, 39, 0.10);
  padding: 8px 10px;
  vertical-align: top;
}

.md th {
  background: rgba(17, 24, 39, 0.04);
  font-weight: 950;
}

.md a {
  color: var(--accent);
  font-weight: 800;
  text-decoration: none;
}

.md a:hover {
  text-decoration: underline;
}

.detailActions {
  margin-top: 12px;
  display: flex;
  gap: 10px;
}

.detailActions.compact {
  margin-top: 6px;
}

.detailCompleteToggle {
  width: 32px;
  height: 32px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel);
  color: var(--muted);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  cursor: pointer;
  transition: all 0.15s ease;
}

.detailCompleteToggle:hover {
  border-color: var(--accentBorder);
  color: var(--accent);
}

.detailCompleteToggle.checked {
  background: rgba(16, 185, 129, 0.16);
  border-color: rgba(16, 185, 129, 0.5);
  color: #16a34a;
}

.primaryButton,
.secondaryButton {
  height: var(--control-h-md);
  border-radius: var(--radius-md);
  padding: var(--control-pad-md);
  font-weight: 800;
}

.primaryButton {
  background: var(--cta-gradient);
  border-color: var(--cta-border);
  color: #fff;
}

.secondaryButton {
  background: var(--panel);
}

/* Bottom capture bar */
.bottomDock {
  border-radius: 12px;
  border: 1px solid var(--border);
  background: var(--panel);
  box-shadow: var(--shadow2);
  padding: 10px;
  display: flex;
  align-items: center;
  gap: 10px;
}

.dockIconButton {
  width: 44px;
  height: 44px;
  border-radius: 12px;
  display: grid;
  place-items: center;
  background: var(--panel2);
}

.dockInputWrap {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 6px;
  min-width: 0;
}

.dockInput {
  height: 44px;
  border-radius: 12px;
  border: 1px solid var(--border);
  padding: 0 12px;
  outline: none;
  background: var(--panel2);
}

.dockHint {
  font-size: 11px;
  color: var(--muted);
}

.dockPrimaryButton {
  height: 44px;
  border-radius: 12px;
  padding: 0 14px;
  font-weight: 900;
  background: var(--accent);
  border-color: var(--accentBorder);
  color: #fff;
}

.dockPrimaryButton:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.bottomNav {
  border-top: 1px solid var(--border);
  background: var(--glass);
  backdrop-filter: blur(10px);
  display: grid;
  grid-template-columns: 1fr 1fr 90px 1fr 1fr;
  align-items: center;
  padding: 6px 10px;
  gap: 8px;
}

.navBtn {
  height: 40px;
  border-radius: 10px;
  border: 1px solid transparent;
  background: transparent;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 2px;
  color: var(--muted);
  font-weight: 800;
  font-size: 10px;
}

.navBtn:hover {
  border-color: var(--border);
  background: var(--panelAlpha);
  color: var(--text);
}

.navBtn svg {
  opacity: 0.9;
}

.navPlus {
  height: 46px;
  width: 46px;
  margin: 0 auto;
  border-radius: 14px;
  border: 1px solid var(--accentBorder);
  position: relative;
  overflow: hidden;
  background: var(--accentSoft);
  box-shadow: 0 10px 18px var(--accentMid);
}

.navPlusGlow {
  position: absolute;
  inset: -20px;
  background: conic-gradient(from 180deg, var(--accentBorder), transparent 35%, var(--accentMid));
  filter: blur(10px);
  opacity: 0.35;
}

.navPlus svg {
  position: relative;
  color: #fff;
}

.rightExpand {
  position: fixed;
  right: 14px;
  top: 14px;
  width: 42px;
  height: 42px;
  border-radius: 14px;
  background: var(--glass);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(17, 24, 39, 0.12);
  box-shadow: 0 10px 22px rgba(17, 24, 39, 0.08);
  display: grid;
  place-items: center;
}

.captureFab {
  position: fixed;
  left: 50%;
  bottom: 18px;
  transform: translateX(-50%);
  width: 46px;
  height: 46px;
  border-radius: 16px;
  background: #D95D39 !important;
  border: none;
  color: #fff;
  box-shadow: 0 8px 20px rgba(217, 93, 57, 0.35);
  display: grid;
  place-items: center;
  z-index: 44;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.captureFab:hover {
  background: #D95D39 !important;
  transform: translateX(-50%) translateY(-2px) scale(1.05);
}

.captureFab:active {
  background: #D95D39 !important;
  transform: translateX(-50%) scale(0.98);
}

.captureFab:focus {
  background: #D95D39 !important;
  outline: none;
}

.captureFab svg {
  transition: transform 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

.captureFab:hover svg {
  transform: rotate(90deg);
}

.modalOverlay {
  position: fixed;
  inset: 0;
  background: var(--overlay);
  backdrop-filter: blur(6px);
  display: grid;
  place-items: center;
  z-index: 50;
}

.docOverlay {
  padding: 0;
  place-items: stretch;
  background: var(--bg);
  backdrop-filter: none;
}

.modalCard {
  width: min(720px, calc(100vw - 28px));
  border-radius: 18px;
  border: 1px solid rgba(255, 255, 255, 0.18);
  background: var(--glass);
  box-shadow: 0 30px 60px rgba(15, 23, 42, 0.25);
  overflow: hidden;
}

.docCard {
  width: 100%;
  height: 100%;
  border-radius: 0;
  border: none;
  background: var(--bg);
  box-shadow: none;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

/* Capture Modal Polish - Glassmorphism */
.captureModalCard {
  background: var(--glass, linear-gradient(165deg, var(--panel) 0%, var(--panel2) 100%));
  backdrop-filter: blur(24px) saturate(180%);
  -webkit-backdrop-filter: blur(24px) saturate(180%);
  border: 1px solid var(--border);
  border-radius: 28px;
  box-shadow:
    0 8px 32px var(--panelAlpha, rgba(0, 0, 0, 0.1)),
    0 0 80px -20px var(--accentSoft);
}

/* Fallback for browsers without backdrop-filter */
@supports not (backdrop-filter: blur(1px)) {
  .captureModalCard {
    background: var(--panel);
  }
}

.captureModalHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 16px 20px;
  border-bottom: 1px solid var(--border);
  background: color-mix(in srgb, var(--panel2) 70%, transparent);
}

.captureModalHeaderLeft {
  display: flex;
  align-items: center;
  gap: 12px;
}

.captureLivePill {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  padding: 4px 10px;
  font-size: 11px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  border-radius: 999px;
  color: var(--muted);
  border: 1px solid var(--border);
  background: color-mix(in srgb, var(--panel) 80%, transparent);
}

.captureLivePill.active {
  color: var(--accent);
  border-color: var(--accentBorder);
  background: var(--accentSoft);
}

.captureLiveDot {
  width: 8px;
  height: 8px;
  border-radius: 999px;
  background: var(--muted);
}

.captureLivePill.active .captureLiveDot {
  background: var(--accent);
  box-shadow: 0 0 10px color-mix(in srgb, var(--accent) 60%, transparent);
}

.captureAttachRow {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  font-size: 12px;
  font-weight: 700;
  color: var(--muted);
  border-bottom: 1px solid var(--border);
  background: color-mix(in srgb, var(--panel) 70%, transparent);
}

.captureAttachLabel {
  text-transform: uppercase;
  letter-spacing: 0.08em;
  font-size: 11px;
}

.captureAttachTitle {
  color: var(--text);
  font-weight: 800;
}

.captureAttachDetach {
  margin-left: auto;
  width: 24px;
  height: 24px;
  border-radius: 8px;
  display: grid;
  place-items: center;
  color: var(--muted);
  border: 1px solid var(--border);
  background: color-mix(in srgb, var(--panel) 80%, transparent);
}

.captureAttachDetach:hover {
  color: var(--text);
  border-color: var(--accentBorder);
}

.captureModalBody {
  display: flex;
  flex-direction: column;
  gap: 16px;
  padding: 18px 20px 22px;
  overflow: hidden;
}

.captureSheet {
  border-radius: 20px;
  border: 1px solid var(--border);
  background: linear-gradient(180deg, color-mix(in srgb, var(--panel) 95%, transparent) 0%, color-mix(in srgb, var(--panel2) 92%, transparent) 100%);
  padding: 16px 18px;
  box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.35);
}

.captureSheetHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 10px;
}

.captureSheetTitle {
  font-size: 11px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.16em;
  color: var(--muted);
}

.captureSheetMeta {
  display: inline-flex;
  align-items: center;
  gap: 10px;
}

.captureSheetStatus {
  font-size: 11px;
  font-weight: 800;
  padding: 2px 8px;
  border-radius: 999px;
  background: color-mix(in srgb, var(--panel) 70%, transparent);
  border: 1px solid var(--border);
  color: var(--muted);
}

.captureSheetStatus.active {
  background: var(--accentSoft);
  border-color: var(--accentBorder);
  color: var(--accent);
}

.captureSheetCount {
  font-size: 11px;
  color: var(--muted2);
  font-weight: 700;
}

.captureTextarea {
  width: 100%;
  min-height: 180px;
  max-height: 280px;
  background: transparent;
  border: none;
  outline: none;
  resize: none;
  font-weight: 600;
  color: var(--text);
  line-height: 1.6;
}

.captureInterim {
  margin-top: 14px;
  padding: 10px 12px;
  border-radius: 12px;
  border: 1px dashed color-mix(in srgb, var(--accent) 40%, transparent);
  background: color-mix(in srgb, var(--accentSoft) 45%, transparent);
  color: var(--muted);
  font-size: 13px;
  font-weight: 700;
  animation: fadeIn 0.2s ease-out;
}

.captureInterimLead {
  font-weight: 900;
  margin-right: 6px;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(6px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.capturePreviewPanel {
  border-radius: 20px;
  border: 1px solid var(--border);
  background: color-mix(in srgb, var(--panel2) 85%, transparent);
  padding: 14px 16px 16px;
  max-height: 240px;
  overflow-y: auto;
}

.captureStatusBar {
  padding: 10px 20px;
  background: color-mix(in srgb, var(--panel2) 80%, transparent);
  border-top: 1px solid var(--border);
  font-size: 11px;
  font-weight: 800;
  letter-spacing: 0.02em;
}

.captureFooter {
  padding: 14px 18px 16px;
  border-top: 1px solid var(--border);
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  background: var(--panel);
}

.captureOutline {
  display: flex;
  flex-direction: column;
  gap: 14px;
}

.captureOutline.compact {
  gap: 10px;
}

.captureOutlineHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
}

.captureOutlineTitle {
  font-size: 12px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.captureOutlineMeta {
  display: inline-flex;
  flex-wrap: wrap;
  gap: 6px;
}

.captureOutlineMetaChip {
  font-size: 11px;
  font-weight: 800;
  padding: 2px 8px;
  border-radius: 999px;
  background: color-mix(in srgb, var(--panel) 70%, transparent);
  border: 1px solid var(--border);
  color: var(--muted);
}

.captureOutlineMetaChip.active {
  background: var(--accentSoft);
  border-color: var(--accentBorder);
  color: var(--accent);
}

.captureOutlineList {
  display: flex;
  flex-direction: column;
  gap: 14px;
}

.captureOutlineItem {
  display: flex;
  gap: 12px;
  align-items: flex-start;
}

.captureOutlineBullet {
  width: 10px;
  height: 10px;
  border-radius: 999px;
  margin-top: 7px;
  background: var(--accent);
  box-shadow: 0 0 0 5px color-mix(in srgb, var(--accentSoft) 70%, transparent);
}

.captureOutlineContent {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.captureOutlineLine {
  display: flex;
  align-items: center;
  gap: 10px;
  font-size: 14px;
  font-weight: 700;
  color: var(--text);
}

.captureOutlineLine.compact {
  font-size: 13px;
}

.captureOutlineIndex {
  font-size: 11px;
  font-weight: 900;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted2);
}

.captureOutlineSnippet {
  color: var(--text);
  font-weight: 700;
  line-height: 1.4;
}

.captureOutlineChips {
  display: flex;
  flex-wrap: wrap;
  gap: 6px;
}

.captureOutlineKinds {
  display: flex;
  flex-wrap: wrap;
  gap: 6px;
}

.captureOutlineKind {
  font-size: 10px;
  font-weight: 800;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  padding: 2px 8px;
  border-radius: 999px;
  border: 1px solid var(--border);
  color: var(--muted);
  background: color-mix(in srgb, var(--panel) 80%, transparent);
}

.captureOutlineKind.question {
  color: rgba(180, 83, 9, 0.95);
  border-color: rgba(245, 158, 11, 0.35);
  background: rgba(251, 191, 36, 0.12);
}

.captureOutlineKind.event {
  color: rgba(67, 56, 202, 0.95);
  border-color: rgba(99, 102, 241, 0.35);
  background: rgba(99, 102, 241, 0.12);
}

.captureOutlineKind.task {
  color: rgba(5, 150, 105, 0.95);
  border-color: rgba(16, 185, 129, 0.35);
  background: rgba(16, 185, 129, 0.12);
}

.captureOutlineKind.habit {
  color: rgba(190, 24, 93, 0.95);
  border-color: rgba(236, 72, 153, 0.35);
  background: rgba(236, 72, 153, 0.12);
}

.captureOutlineKind.tracker {
  color: rgba(67, 56, 202, 0.95);
  border-color: rgba(99, 102, 241, 0.35);
  background: rgba(99, 102, 241, 0.12);
}

.captureOutlineTrackerValue {
  margin-left: 4px;
  padding: 0 4px;
  border-radius: 6px;
  background: rgba(99, 102, 241, 0.12);
  color: rgba(67, 56, 202, 0.95);
  font-weight: 900;
}

.captureOutlineSublist {
  display: flex;
  flex-direction: column;
  gap: 6px;
  margin-top: 2px;
}

.captureOutlineSubItem {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 12px;
  font-weight: 600;
  color: var(--text);
}

.captureOutlineSubText {
  flex: 1;
  min-width: 0;
}

.captureOutlineSubTime {
  font-size: 11px;
  color: var(--muted2);
  font-weight: 700;
}

.captureOutlineSubBadge {
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  padding: 1px 6px;
  border-radius: 999px;
  background: rgba(16, 185, 129, 0.15);
  color: rgba(5, 150, 105, 0.9);
}

.captureOutlineSubMore {
  font-size: 11px;
  color: var(--muted2);
  font-weight: 600;
}

.mdChip-tracker {
  background: rgba(99, 102, 241, 0.12);
  border-color: rgba(99, 102, 241, 0.24);
  color: rgba(67, 56, 202, 0.9);
}

/* Recording state - breathing glow */
.captureModalCard.recording {
  animation: breathingGlow 3s ease-in-out infinite;
}

@keyframes breathingGlow {
  0%, 100% {
    box-shadow:
      0 8px 32px var(--panelAlpha, rgba(0, 0, 0, 0.1)),
      0 0 40px var(--accentSoft);
  }
  50% {
    box-shadow:
      0 8px 32px var(--panelAlpha, rgba(0, 0, 0, 0.1)),
      0 0 60px var(--accent);
  }
}

/* Extended toggle button */
.extended-toggle {
  display: flex;
  align-items: center;
  gap: 6px;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 700;
  background: var(--panel2);
  border: 1px solid var(--border);
  color: var(--muted);
  cursor: pointer;
  transition: all 0.2s ease;
}

.extended-toggle:hover {
  border-color: var(--accent);
}

.extended-toggle.active {
  background: var(--accentSoft);
  border-color: var(--accent);
  color: var(--accent);
}

/* Waveform visualization */
.waveform-container {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 3px;
  height: 32px;
  padding: 8px 24px;
}

.waveform-bar {
  width: 4px;
  background: linear-gradient(to top, var(--accent), var(--accentSoft));
  border-radius: 2px;
  animation: waveform 1.2s ease-in-out infinite;
}

.waveform-bar:nth-child(1) { animation-delay: 0s; }
.waveform-bar:nth-child(2) { animation-delay: 0.1s; }
.waveform-bar:nth-child(3) { animation-delay: 0.2s; }
.waveform-bar:nth-child(4) { animation-delay: 0.3s; }
.waveform-bar:nth-child(5) { animation-delay: 0.4s; }
.waveform-bar:nth-child(6) { animation-delay: 0.3s; }
.waveform-bar:nth-child(7) { animation-delay: 0.2s; }
.waveform-bar:nth-child(8) { animation-delay: 0.1s; }
.waveform-bar:nth-child(9) { animation-delay: 0s; }

@keyframes waveform {
  0%, 100% { transform: scaleY(0.3); opacity: 0.5; }
  50% { transform: scaleY(1); opacity: 1; }
}

/* Entity chip animations */
.entity-chip-enter {
  animation: chipAppear 0.3s ease-out;
}

@keyframes chipAppear {
  from { opacity: 0; transform: translateY(8px) scale(0.9); }
  to { opacity: 1; transform: translateY(0) scale(1); }
}

.captureVoiceBtn {
  position: relative;
  overflow: hidden;
}
.captureVoiceBtn::before {
  content: '';
  position: absolute;
  inset: 0;
  background: radial-gradient(circle at center, var(--accentSoft) 0%, transparent 70%);
  opacity: 0;
  transition: opacity 0.3s ease;
}
.captureVoiceBtn:hover::before {
  opacity: 0.5;
}
.captureVoiceBtnActive {
  animation: voicePulse 1.5s ease-in-out infinite;
}
.captureVoiceBtnActive::before {
  opacity: 1;
}

@keyframes voicePulse {
  0%, 100% {
    box-shadow: 0 0 0 0 var(--accentSoft), 0 4px 12px rgba(0, 0, 0, 0.15);
  }
  50% {
    box-shadow: 0 0 0 8px transparent, 0 4px 16px rgba(0, 0, 0, 0.2);
  }
}

.modalHeader {
  padding: 10px 12px 8px;
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  gap: 10px;
  border-bottom: 1px solid var(--border);
  background: var(--panel);
}

.modalHeaderActions {
  display: flex;
  align-items: center;
  gap: 8px;
}

.modalMagic {
  height: 30px;
  padding: 0 10px;
  font-size: 12px;
}

.modalTitle {
  font-weight: 950;
  letter-spacing: 0.2px;
}

.modalSub {
  margin-top: 4px;
  font-size: 12px;
  color: var(--muted);
}

.capAttachBanner {
  margin-top: 8px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 10px;
  padding: 8px 10px;
  border-radius: 12px;
  background: var(--accentSoft);
  border: 1px solid var(--accentMid);
}

.capAttachLabel {
  font-size: 12px;
  font-weight: 900;
  color: var(--text);
