}

.modalClose {
  width: 36px;
  height: 36px;
  border-radius: 12px;
  display: grid;
  place-items: center;
  background: rgba(17, 24, 39, 0.04);
  border: 1px solid rgba(17, 24, 39, 0.08);
}

.modalTextarea {
  width: 100%;
  border: none;
  outline: none;
  padding: 12px;
  min-height: 190px;
  font-size: 12px;
  line-height: 1.4;
  background: transparent;
  resize: vertical;
}

.capEditor {
  position: relative;
}

.capEditorInterim {
  position: absolute;
  left: 12px;
  right: 12px;
  bottom: 10px;
  padding: 6px 8px;
  border-radius: 10px;
  border: 1px solid rgba(17, 24, 39, 0.10);
  background: rgba(255, 255, 255, 0.72);
  color: var(--muted);
  font-size: 11px;
  font-weight: 800;
  pointer-events: none;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

:root[data-theme='dark'] .capEditorInterim {
  background: rgba(11, 16, 32, 0.68);
  border-color: rgba(148, 163, 184, 0.08);
}

.capPreview {
  padding: 0 12px 10px;
}

.eventBody {
  padding: 12px;
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.eventTitleRow {
  display: flex;
}

.eventTitleInput {
  width: 100%;
  height: 40px;
  border-radius: 12px;
  border: 1px solid rgba(17, 24, 39, 0.10);
  padding: 0 12px;
  font-weight: 950;
  background: var(--glass3);
  outline: none;
  color: var(--text);
}

.eventGrid {
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 10px;
}

@media (max-width: 720px) {
  .eventGrid {
    grid-template-columns: 1fr;
  }
}

.eventGrid label {
  display: flex;
  flex-direction: column;
  gap: 6px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 700;
}

.eventCheck {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 800;
}

.eventNotes {
  display: flex;
  flex-direction: column;
  gap: 6px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 700;
}

.eventNotesHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 8px;
}

.eventNotesArea {
  width: 100%;
  min-height: 120px;
  border-radius: 12px;
  border: 1px solid rgba(17, 24, 39, 0.10);
  padding: 10px;
  font-size: 12px;
  line-height: 1.4;
  background: var(--glass3);
  outline: none;
  color: var(--text);
  resize: vertical;
}

.capInterim {
  padding: 0 12px 10px;
  color: var(--muted);
  font-size: 11px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.capProgress {
  padding: 0 12px 10px;
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.capProgressLine {
  font-size: 11px;
  font-weight: 800;
  color: var(--muted);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.capPreviewTitle {
  font-size: 11px;
  font-weight: 900;
  color: var(--muted);
  letter-spacing: 0.6px;
  text-transform: uppercase;
  margin-bottom: 8px;
}

.capPreviewList {
  display: flex;
  flex-direction: column;
  gap: 6px;
  max-height: 180px;
  overflow: auto;
  padding-right: 4px;
}

.capItem {
  display: grid;
  grid-template-columns: 18px 52px 1fr auto;
  align-items: center;
  gap: 10px;
  padding: 8px 10px;
  border-radius: 12px;
  border: 1px solid rgba(17, 24, 39, 0.08);
  background: rgba(17, 24, 39, 0.03);
  cursor: pointer;
}

:root[data-theme='dark'] .capItem {
  border-color: rgba(148, 163, 184, 0.08);
  background: rgba(148, 163, 184, 0.06);
}

.capKind {
  height: 20px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 0 8px;
  border-radius: 999px;
  font-size: 11px;
  font-weight: 900;
  color: var(--text);
  border: 1px solid rgba(17, 24, 39, 0.10);
  background: rgba(255, 255, 255, 0.65);
}

:root[data-theme='dark'] .capKind {
  border-color: rgba(148, 163, 184, 0.08);
  background: rgba(11, 16, 32, 0.72);
}

.capTitle {
  font-weight: 900;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  min-width: 0;
}

.capMeta {
  font-size: 12px;
  font-weight: 800;
  color: var(--muted);
  white-space: nowrap;
}

.modalActions {
  padding: 10px 12px 12px;
  border-top: 1px solid rgba(17, 24, 39, 0.08);
  display: flex;
  align-items: center;
  gap: 10px;
}

.capStatus {
  padding: 8px 14px 0;
  font-size: 12px;
  font-weight: 800;
  color: var(--muted);
}

.modalSpacer {
  flex: 1;
}

.modalMic {
  height: 40px;
  border-radius: 999px;
  padding: 0 12px;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  font-weight: 900;
  background: rgba(17, 24, 39, 0.04);
  border: 1px solid rgba(17, 24, 39, 0.10);
  position: relative;
  overflow: hidden;
}

.modalMic.active {
  background: var(--accentMid);
  border-color: var(--accentBorder);
}

.modalMic.magicRecord::before {
  content: '';
  position: absolute;
  inset: 1px;
  border-radius: inherit;
  background: linear-gradient(135deg, var(--accentSoft), rgba(236, 72, 153, 0.08));
  opacity: 0.6;
  pointer-events: none;
}

.modalMic.magicRecord::after {
  content: '';
  position: absolute;
  inset: -10%;
  border-radius: inherit;
  border: 1px solid var(--accentBorder);
  opacity: 0;
  transform: scale(0.85);
  pointer-events: none;
}

.modalMic.magicRecord.active::after {
  animation: magicPulse 1.6s ease-out infinite;
}

@keyframes magicPulse {
  0% {
    opacity: 0.65;
    transform: scale(0.85);
  }
  70% {
    opacity: 0;
    transform: scale(1.25);
  }
  100% {
    opacity: 0;
    transform: scale(1.25);
  }
}

.docGrid {
  flex: 1;
  min-height: 0;
  display: flex;
  flex-direction: row;
  gap: 16px;
  padding: 16px;
  overflow: auto;
}

/* Responsive: stack vertically on narrow screens */
