}

.docBody .mdEditor {
  flex: 1;
  min-height: 0;
  background: var(--panel2);
  border-color: var(--border);
}

.docBody .mdEditorTop {
  background: var(--panel2);
  border-bottom: 1px solid var(--border);
  justify-content: flex-start;
}

.docBody .mdTextarea {
  min-height: 0;
  resize: none;
  background: transparent;
}

.docBody .mdPreviewPane {
