  gap: 4px;
  overflow: hidden;
}

.notesTableTag {
  padding: 2px 6px;
  background: var(--panel2);
  border-radius: 4px;
  font-size: 10px;
  font-weight: 600;
  color: var(--muted);
  white-space: nowrap;
}

.notesTableCell.preview {
  color: var(--muted);
  font-size: 11px;
}

/* Responsive */
