@media (max-width: 980px) {
  .dashGrid {
    grid-template-columns: 1fr;
  }
}

.dashCard {
  border: 1px solid var(--border);
  border-radius: 16px;
  background: linear-gradient(180deg, var(--panel), rgba(255, 255, 255, 0.9));
  padding: 12px;
  overflow: hidden;
  box-shadow: 0 18px 32px rgba(15, 23, 42, 0.08);
  transition: transform 140ms ease, box-shadow 140ms ease;
}

.dashCardTitle {
  font-size: 12px;
  font-weight: 900;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  color: var(--muted);
  margin-bottom: 8px;
}

.dashRow {
  display: grid;
  grid-template-columns: 240px 1fr;
  gap: 12px;
  align-items: center;
}

@media (max-width: 980px) {
  .dashRow {
    grid-template-columns: 1fr;
  }
}

.dashLegend {
  display: flex;
  flex-direction: column;
  gap: 6px;
  padding-right: 4px;
}

.dashLegend.compact {
  margin-top: 8px;
}

.dashLegendRow {
  display: flex;
  justify-content: space-between;
  gap: 10px;
  font-size: 12px;
  font-weight: 850;
  color: var(--text);
}

.dashLegendKey {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  color: var(--text);
}

.dashLegendVal {
  color: var(--muted);
  font-weight: 900;
  white-space: nowrap;
}

.dashPie {
  width: 220px;
  height: 220px;
  display: block;
}

.dashPieTotal {
  font-size: 22px;
  font-weight: 950;
  fill: rgba(17, 24, 39, 0.92);
}

.dashPieSub {
  font-size: 12px;
  font-weight: 900;
  fill: rgba(107, 114, 128, 0.95);
}

.dashCard:hover {
  transform: translateY(-1px);
  box-shadow: 0 22px 36px rgba(15, 23, 42, 0.12);
}

.dashWidgetBtn:hover,
.dashHiddenBtn:hover,
.dashRangeBtn:hover {
  transform: translateY(-1px);
  box-shadow: 0 10px 18px rgba(15, 23, 42, 0.12);
}

:root[data-theme='dark'] .dashCard {
  background: linear-gradient(180deg, rgba(11, 16, 32, 0.98), rgba(11, 16, 32, 0.9));
  box-shadow: 0 18px 32px rgba(0, 0, 0, 0.3);
}

:root[data-theme='dark'] .dashPieTotal {
  fill: rgba(226, 232, 240, 0.9);
}

:root[data-theme='dark'] .dashPieSub {
  fill: rgba(148, 163, 184, 0.8);
}

.dashSmallNote {
  margin-top: 6px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 800;
}

.dashRadarWrap {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.dashRadar {
  width: 260px;
  height: 260px;
  display: block;
  margin: 0 auto;
}

.dashRadarLabel {
  font-size: 11px;
  font-weight: 900;
  fill: rgba(107, 114, 128, 0.95);
}

.dashList {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.dashListRow {
  display: flex;
  justify-content: space-between;
  gap: 10px;
  font-size: 13px;
  font-weight: 850;
  border: 1px solid rgba(17, 24, 39, 0.10);
  background: rgba(17, 24, 39, 0.03);
  border-radius: 12px;
  padding: 8px 10px;
}

:root[data-theme='dark'] .dashListRow {
  border-color: rgba(148, 163, 184, 0.08);
  background: rgba(148, 163, 184, 0.06);
}

.dashListKey {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.dashListVal {
  color: var(--muted);
  font-weight: 900;
  white-space: nowrap;
}

.dashEmpty {
  font-size: 12px;
  color: var(--muted);
  font-weight: 900;
  padding: 6px 2px;
}

/* Life Tracker card grid (light) */
.lt-container {
  padding: 14px;
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.lt-titlebar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
}

.lt-title {
  font-weight: 900;
}

.lt-title-count {
  font-size: 12px;
  color: var(--muted);
  margin-left: 8px;
}

.lt-title-actions {
  display: flex;
  gap: 8px;
}

.lt-title-btn {
  height: 30px;
  border-radius: 999px;
  padding: 0 10px;
  background: var(--panel2);
  color: var(--muted);
  font-weight: 900;
}

.lt-title-btn.primary {
  background: var(--accentSoft);
  border-color: var(--accentMid);
  color: var(--text);
}

.lt-control-bar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  padding-bottom: 10px;
  border-bottom: 1px solid var(--border);
}

.lt-mini {
  font-size: 12px;
  color: var(--muted);
}

.lt-control-bar-right {
  display: flex;
  gap: 10px;
  align-items: center;
}

.lt-control-group {
  display: flex;
  gap: 8px;
  align-items: center;
}

.lt-control-value {
  font-size: 12px;
  font-weight: 900;
  color: var(--muted);
}

.lt-control-btn {
  width: 30px;
  height: 30px;
  border-radius: 10px;
  display: grid;
  place-items: center;
  background: var(--panel2);
}

.lt-grid {
  display: grid;
  gap: 12px;
  --lt-grid-columns: 2;
  --lt-card-min-height: 240px;
  grid-template-columns: repeat(var(--lt-grid-columns), 1fr);
}

.lt-card {
  border: 1px solid var(--border);
  border-radius: 12px;
  background: var(--panel);
  padding: 12px;
  min-height: var(--lt-card-min-height);
}

.lt-section {
  height: 100%;
  display: flex;
  flex-direction: column;
}

.lt-section-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-bottom: 10px;
  border-bottom: 1px solid var(--border);
}

.lt-section-title {
  font-size: 12px;
  font-weight: 900;
  color: var(--muted);
  letter-spacing: 0.7px;
}

.lt-section-actions {
  display: flex;
  gap: 6px;
}

.lt-play-btn,
.lt-maximize-btn {
  width: 30px;
  height: 30px;
  border-radius: 10px;
  border: none;
  background: transparent;
  color: var(--muted);
  cursor: pointer;
}

.lt-play-btn:hover,
.lt-maximize-btn:hover {
  background: rgba(17, 24, 39, 0.04);
  color: var(--text);
}

.lt-play-btn--playing {
  color: var(--accent);
  animation: lt-pulse 1s ease-in-out infinite;
}

@keyframes lt-pulse {
  0%,
  100% {
    opacity: 1;
  }
  50% {
    opacity: 0.55;
  }
}

.lt-svg {
  width: 100%;
  flex: 1;
}

.lt-gridline {
  stroke: rgba(17, 24, 39, 0.10);
}

.lt-heat-label {
  fill: rgba(17, 24, 39, 0.50);
  font-size: 11px;
}

.lt-quick-grid {
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 10px;
  margin-top: 10px;
}

.lt-quick-tile {
  text-align: left;
  border-radius: 12px;
  border: 1px solid var(--border);
  background: var(--panel2);
  padding: 10px;
}

.lt-quick-emoji {
  font-size: 16px;
}

.lt-quick-label {
  margin-top: 8px;
  font-weight: 900;
}

.lt-quick-hint {
  margin-top: 6px;
  font-size: 12px;
  color: var(--muted);
  font-family: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, 'Liberation Mono', 'Courier New', monospace;
}

.lt-history {
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-top: 10px;
  overflow: auto;
  padding-right: 2px;
}

.lt-history-empty {
  margin-top: 10px;
  color: var(--muted);
  font-size: 13px;
}

.lt-history-item {
  text-align: left;
  border-radius: 12px;
  border: 1px solid var(--border);
  background: var(--panel2);
  padding: 10px;
}

.lt-history-item:hover {
  border-color: var(--accentMid);
  background: var(--accentSoft);
}

.lt-history-top {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 10px;
}

.lt-history-time {
  font-size: 12px;
  color: var(--muted);
  font-weight: 900;
}

.lt-history-action {
  font-size: 12px;
  font-weight: 900;
  color: var(--accent);
}

.lt-history-text {
  margin-top: 8px;
  color: var(--text);
  font-size: 13px;
  line-height: 1.25;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.lt-workout-start {
  margin-top: 12px;
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.lt-workout-input {
  height: 36px;
  border-radius: 10px;
  border: 1px solid var(--border);
  padding: 0 10px;
  background: var(--panel2);
  color: var(--text);
  font-size: 13px;
  flex: 1;
  min-width: 180px;
}

.lt-workout-input.sm {
  height: 32px;
  font-size: 12px;
  min-width: 80px;
  flex: 0 0 auto;
}

.lt-workout-btn {
  height: 36px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel2);
  color: var(--text);
  font-weight: 900;
  font-size: 12px;
  padding: 0 12px;
  cursor: pointer;
}

.lt-workout-btn.primary {
  background: var(--accentSoft);
  border-color: var(--accentMid);
}

.lt-workout-active {
  margin-top: 12px;
  display: flex;
  flex-direction: column;
  gap: 12px;
  height: 100%;
}

.lt-workout-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
}

.lt-workout-title {
  font-weight: 900;
  font-size: 15px;
}

.lt-workout-meta {
  font-size: 12px;
  color: var(--muted);
  margin-top: 4px;
}

.lt-workout-add {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  align-items: center;
}

.lt-workout-exercises {
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-top: 4px;
  overflow: auto;
  padding-right: 2px;
  flex: 1;
}

.lt-workout-empty {
  font-size: 12px;
  color: var(--muted);
}

.lt-workout-exercise {
  border: 1px solid var(--border);
  border-radius: 12px;
  background: var(--panel2);
  padding: 10px;
}

.lt-workout-ex-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 8px;
}

.lt-workout-ex-name {
  font-weight: 900;
}

.lt-workout-remove {
  width: 26px;
  height: 26px;
  border-radius: 8px;
  border: none;
  background: transparent;
  color: var(--muted);
  cursor: pointer;
}

.lt-workout-remove:hover {
  background: rgba(17, 24, 39, 0.06);
  color: var(--text);
}

.lt-workout-sets {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-top: 8px;
}

.lt-workout-set {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 8px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel);
  padding: 6px 8px;
  font-size: 12px;
}

.lt-workout-set-inputs {
  display: flex;
  flex-wrap: wrap;
  gap: 6px;
  align-items: center;
}

.lt-workout-prs {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-top: 10px;
}

.lt-workout-pr {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 10px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel2);
  padding: 8px 10px;
  font-size: 13px;
}

.lt-imports {
  margin-top: 10px;
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.lt-import-row {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  align-items: center;
}

.lt-import-btn {
  height: 36px;
  border-radius: 10px;
  border: 1px solid var(--accentMid);
  background: var(--accentSoft);
  color: var(--text);
  font-weight: 900;
  font-size: 12px;
  padding: 0 12px;
  display: inline-flex;
  align-items: center;
  cursor: pointer;
}

.lt-import-btn input {
  display: none;
}

.lt-import-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.lt-import-item {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel2);
  padding: 8px 10px;
}

.lt-import-provider {
  font-weight: 900;
  font-size: 13px;
}

.lt-import-meta {
  font-size: 12px;
  color: var(--muted);
}

.lt-container--has-maximized::before {
  content: '';
  position: fixed;
  inset: 0;
  background: rgba(17, 24, 39, 0.45);
  z-index: 40;
}

.lt-card--hidden {
  display: none;
}

.lt-card--maximized {
  position: fixed;
  inset: 18px;
  z-index: 50;
  overflow: auto;
  min-height: auto;
  padding: 18px;
  background: var(--panel);
}

.goalSection {
  background: var(--panel2);
  border: 1px solid var(--border);
  border-radius: 24px;
  padding: 18px;
  display: grid;
  gap: 16px;
}

.goalSectionPlan {
  padding: 22px;
  min-height: 420px;
}

.goalChipRow {
  display: flex;
  align-items: center;
  gap: 8px;
  flex-wrap: wrap;
}

.goalChip {
  padding: 6px 12px;
  border-radius: 999px;
  border: 1px solid var(--border);
  background: var(--panel);
  font-size: 11px;
  font-weight: 800;
  color: var(--muted);
  transition: border-color 120ms ease, color 120ms ease, box-shadow 120ms ease;
}

.goalChip:hover {
  color: var(--text);
  border-color: var(--border2);
  box-shadow: 0 10px 18px rgba(15, 23, 42, 0.08);
}

.goalChip.active {
  color: #D95D39;
  border-color: rgba(217, 93, 57, 0.35);
  box-shadow: 0 10px 18px rgba(217, 93, 57, 0.12);
}

.goalChipMeta {
  font-size: 10px;
  font-weight: 800;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted);
}

.goalLimitNote {
  font-size: 10px;
  font-weight: 800;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted);
}

.goalSectionHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
}

.goalSectionEyebrow {
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.18em;
  color: var(--muted);
}

.goalSectionTitle {
  font-size: 15px;
  font-weight: 900;
  color: var(--text);
}

.goalSectionTitleRow {
  display: flex;
  align-items: center;
  gap: 8px;
}

.goalPlanAssistToggle {
  width: 28px;
  height: 28px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel);
  color: var(--muted);
  display: grid;
  place-items: center;
  transition: border-color 120ms ease, color 120ms ease, box-shadow 120ms ease;
}

.goalPlanAssistToggle:hover {
  color: var(--text);
  border-color: var(--border2);
  box-shadow: 0 8px 18px rgba(15, 23, 42, 0.08);
}

.goalPlanAssistToggle.active {
  color: #D95D39;
  border-color: rgba(217, 93, 57, 0.4);
  box-shadow: 0 10px 18px rgba(217, 93, 57, 0.12);
}

.goalSectionMeta {
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalKpiGrid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
  gap: 12px;
}

.goalKpiCard {
  border-radius: 18px;
  border: 1px solid var(--border);
  background: var(--panel);
  padding: 14px;
  display: grid;
  gap: 6px;
}

.goalKpiLabel {
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalKpiValue {
  font-size: 20px;
  font-weight: 900;
  color: var(--text);
}

.goalKpiMeta {
  font-size: 11px;
  font-weight: 800;
  color: var(--muted);
}

.goalChecklist {
  display: grid;
  gap: 8px;
  overflow-x: auto;
  padding-bottom: 6px;
}

.goalChecklistRow {
  display: grid;
  grid-template-columns: 120px repeat(var(--goal-cols, 3), minmax(120px, 1fr));
  align-items: center;
  gap: 8px;
  min-width: max-content;
}

.goalChecklistHeader {
  padding-bottom: 8px;
  border-bottom: 1px dashed var(--border);
}

.goalChecklistDay {
  font-size: 11px;
  font-weight: 800;
  color: var(--text);
}

.goalChecklistCol {
  font-size: 10px;
  font-weight: 800;
  color: var(--muted);
  text-transform: uppercase;
  letter-spacing: 0.12em;
}

.goalChecklistCell {
  display: flex;
  align-items: center;
  gap: 6px;
  position: relative;
}

.goalCheck {
  width: 26px;
  height: 26px;
  border-radius: 8px;
  border: 1px solid rgba(17, 24, 39, 0.18);
  background: rgba(255, 255, 255, 0.75);
  color: rgba(17, 24, 39, 0.55);
  display: grid;
  place-items: center;
}

.goalCheck.checked {
  border-color: rgba(16, 185, 129, 0.4);
  background: rgba(16, 185, 129, 0.16);
  color: #166534;
}

.goalCheckLink {
  width: 24px;
  height: 24px;
  border-radius: 8px;
  border: 1px solid rgba(17, 24, 39, 0.14);
  background: rgba(255, 255, 255, 0.75);
  color: var(--muted);
  display: grid;
  place-items: center;
  transition: border-color 120ms ease, color 120ms ease;
}

.goalCheckLink:hover {
  color: var(--text);
  border-color: rgba(17, 24, 39, 0.24);
}

.goalCheckMenu {
  width: 24px;
  height: 24px;
  border-radius: 8px;
  border: 1px solid rgba(17, 24, 39, 0.14);
  background: rgba(255, 255, 255, 0.75);
  color: var(--muted);
  display: grid;
  place-items: center;
  transition: border-color 120ms ease, color 120ms ease;
}

.goalCheckMenu:hover {
  color: var(--text);
  border-color: rgba(17, 24, 39, 0.24);
}

.goalCheckMenuPopover {
