.goalCheckMenuItem {
  text-align: left;
  padding: 6px 10px;
  border-radius: 8px;
  font-size: 11px;
  font-weight: 700;
  color: var(--text);
  background: transparent;
}

.goalCheckMenuItem:hover {
  background: rgba(15, 23, 42, 0.06);
}

.goalChecklistNav {
  display: flex;
  align-items: center;
  gap: 10px;
}

.goalChecklistNavButton {
  width: 28px;
  height: 28px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel);
  color: var(--muted);
  display: grid;
  place-items: center;
  transition: border-color 120ms ease, color 120ms ease, box-shadow 120ms ease;
}

.goalChecklistNavButton:hover {
  color: var(--text);
  border-color: var(--border2);
  box-shadow: 0 8px 18px rgba(15, 23, 42, 0.08);
}

.goalChecklistNavText {
  display: grid;
  gap: 2px;
  text-align: center;
}

.goalChecklistNavLabel {
  font-size: 11px;
  font-weight: 800;
  color: var(--text);
}

.goalChecklistNavMeta {
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalPlan {
  display: grid;
  grid-template-columns: minmax(0, 1.2fr) minmax(0, 1fr);
  gap: 16px;
  align-items: stretch;
}

.goalPlanLeft {
  display: grid;
  gap: 12px;
}

.goalPlanAssist {
  border-radius: 16px;
  border: 1px dashed var(--border);
  background: rgba(255, 255, 255, 0.75);
  padding: 12px;
  display: grid;
  gap: 10px;
}

.goalPlanAssistHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 10px;
}

.goalPlanAssistTitle {
  font-size: 12px;
  font-weight: 900;
  color: var(--text);
}

.goalPlanAssistSub {
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalPlanAssistClose {
  width: 26px;
  height: 26px;
  border-radius: 8px;
  border: 1px solid var(--border);
  background: var(--panel);
  color: var(--muted);
  display: grid;
  place-items: center;
  transition: border-color 120ms ease, color 120ms ease, box-shadow 120ms ease;
}

.goalPlanAssistClose:hover {
