  color: var(--muted);
}

.goalPlanAssistInput,
.goalPlanAssistTextarea,
.goalPlanAssistOutput {
  width: 100%;
  border-radius: 12px;
  border: 1px solid var(--border);
  background: #fff;
  padding: 8px 10px;
  font-size: 12px;
  font-weight: 600;
  color: var(--text);
  text-transform: none;
  letter-spacing: normal;
}

.goalPlanAssistTextarea {
  min-height: 72px;
  resize: vertical;
}

.goalPlanAssistOutput {
  min-height: 120px;
  background: rgba(255, 255, 255, 0.9);
  resize: vertical;
}

.goalPlanAssistEmpty {
  border-radius: 12px;
  border: 1px dashed var(--border);
  padding: 12px;
  background: rgba(255, 255, 255, 0.65);
  font-size: 11px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalPlanAssistStatus {
  font-size: 11px;
  font-weight: 700;
  color: var(--muted);
}

.goalPlanAssistError {
  font-size: 11px;
  font-weight: 800;
  color: #B91C1C;
}

.goalPlanAssistActions {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.goalPlanAssistBtn {
  height: 30px;
  padding: 0 12px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel);
  font-size: 11px;
  font-weight: 800;
  color: var(--text);
  transition: border-color 120ms ease, box-shadow 120ms ease;
}

.goalPlanAssistBtn:hover {
  border-color: var(--border2);
  box-shadow: 0 8px 18px rgba(15, 23, 42, 0.08);
}

.goalPlanAssistBtn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  box-shadow: none;
}

.goalPlanAssistBtn.primary {
  background: rgba(217, 93, 57, 0.18);
  border-color: rgba(217, 93, 57, 0.4);
  color: #D95D39;
}

.goalPlanEditor {
  min-height: 240px;
  width: 100%;
  border-radius: 16px;
  border: 1px solid rgba(17, 24, 39, 0.12);
  background: #fff;
  padding: 14px 16px;
  font-size: 12px;
  font-weight: 600;
  line-height: 1.6;
  color: var(--text);
  resize: vertical;
  box-shadow: 0 14px 26px rgba(15, 23, 42, 0.08);
}

.goalPlanOutline {
  border-radius: 16px;
  border: 1px dashed var(--border);
  background: rgba(255, 255, 255, 0.7);
  padding: 12px;
  display: grid;
  gap: 8px;
  max-height: 220px;
  overflow: auto;
}

.goalPlanOutlineEmpty {
  font-size: 11px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalPlanOutlineItem {
  display: grid;
  gap: 4px;
  padding-left: calc(var(--plan-level, 0) * 14px);
}

.goalPlanOutlineTitle {
  font-size: 12px;
  font-weight: 800;
  color: var(--text);
}

.goalPlanOutlineTags {
  display: flex;
  flex-wrap: wrap;
  gap: 6px;
}

.goalPlanTag {
  padding: 2px 6px;
  border-radius: 999px;
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  background: rgba(17, 24, 39, 0.08);
  color: var(--muted);
}

.goalPlanPreview {
  --plan-row-gap: 4px;
  --plan-track-h: 12px;
  --plan-title-size: 11px;
  --plan-date-size: 9px;
  --plan-col-gap: 8px;
  --plan-label-min: 180px;
  --plan-label-max: 260px;
  --plan-bar-pad: 1px;
  border-radius: 16px;
  border: 1px solid var(--border);
  background: var(--panel);
  padding: 10px;
  display: grid;
  gap: 8px;
  grid-template-rows: auto 1fr;
  align-content: start;
  min-height: 240px;
  overflow: auto;
}

.goalPlanPreview.density-tight {
  --plan-row-gap: 2px;
  --plan-track-h: 10px;
  --plan-title-size: 10px;
  --plan-date-size: 8px;
  --plan-col-gap: 6px;
  --plan-label-min: 160px;
  --plan-label-max: 220px;
  --plan-bar-pad: 1px;
}

.goalPlanPreview.density-compact {
  --plan-row-gap: 4px;
  --plan-track-h: 12px;
  --plan-title-size: 11px;
  --plan-date-size: 9px;
  --plan-col-gap: 8px;
  --plan-label-min: 180px;
  --plan-label-max: 260px;
  --plan-bar-pad: 1px;
}

.goalPlanPreview.density-roomy {
  --plan-row-gap: 7px;
  --plan-track-h: 16px;
  --plan-title-size: 12px;
  --plan-date-size: 10px;
  --plan-col-gap: 10px;
  --plan-label-min: 200px;
  --plan-label-max: 300px;
  --plan-bar-pad: 2px;
}

.goalPlanPreviewHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 8px;
}

.goalPlanPreviewHeading {
  display: grid;
  gap: 2px;
}

.goalPlanPreviewTitle {
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalPlanPreviewRange {
  font-size: 10px;
  font-weight: 800;
  color: var(--muted);
}

.goalPlanPreviewActions {
  display: flex;
  align-items: center;
  gap: 6px;
}

.goalPlanPreviewDensity {
  display: flex;
  align-items: center;
  gap: 6px;
  font-size: 9px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalPlanPreviewDensityInput {
  width: 70px;
  height: 4px;
}

.goalPlanPreviewDensityValue {
  font-size: 9px;
  font-weight: 800;
  color: var(--text);
}

.goalPlanPreviewBtn {
  width: 28px;
  height: 28px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel);
  color: var(--muted);
  display: grid;
  place-items: center;
  transition: border-color 120ms ease, color 120ms ease, box-shadow 120ms ease;
}

.goalPlanPreviewBtn:hover {
  color: var(--text);
  border-color: var(--border2);
  box-shadow: 0 8px 18px rgba(15, 23, 42, 0.08);
}

.goalPlanPreviewBackdrop {
  position: fixed;
  inset: 0;
  background: rgba(17, 24, 39, 0.45);
  z-index: 55;
}

.goalPlanPreviewMaximized {
  position: fixed;
  inset: 18px;
  z-index: 60;
  min-height: auto;
  max-height: calc(100vh - 40px);
  overflow: auto;
  padding: 6px;
  box-shadow: 0 24px 40px rgba(15, 23, 42, 0.2);
}

.goalPlanPreviewBody {
  font-size: 12px;
  font-weight: 700;
  color: var(--muted);
}

.goalPlanTimeline {
  display: grid;
  gap: var(--plan-row-gap);
  --goal-plan-grid: 6;
}

.goalPlanTimelineRow {
  display: grid;
  grid-template-columns: minmax(var(--plan-label-min), var(--plan-label-max)) minmax(0, 1fr);
  gap: var(--plan-col-gap);
  align-items: center;
}

.goalPlanTimelineLabel {
  position: relative;
  display: flex;
  align-items: center;
  gap: 6px;
  min-width: 0;
  padding-left: calc(var(--plan-indent, 0px));
}

.goalPlanTimelineTitle {
  flex: 1 1 auto;
  min-width: 0;
  font-size: var(--plan-title-size);
  font-weight: 800;
  color: var(--text);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.goalPlanTimelineDates {
  flex: 0 0 auto;
  font-size: var(--plan-date-size);
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--muted);
}

.goalPlanTimelineLabel.nested::before {
  content: '-';
  position: absolute;
  left: var(--plan-indent, 0px);
  top: 50%;
  transform: translateY(-50%);
  color: var(--muted);
}

.goalPlanTimelineLabel.nested {
  padding-left: calc(var(--plan-indent, 0px) + 10px);
}

.goalPlanTimelineTrack {
  position: relative;
  height: var(--plan-track-h);
  border-radius: 999px;
  background: linear-gradient(90deg, rgba(217, 93, 57, 0.12) 1px, transparent 1px);
  background-size: calc(100% / var(--goal-plan-grid, 6)) 100%;
}

.goalPlanTimelineBar {
  position: absolute;
  top: var(--plan-bar-pad);
  bottom: var(--plan-bar-pad);
  border-radius: 999px;
  background: rgba(217, 93, 57, 0.7);
  box-shadow: 0 6px 14px rgba(217, 93, 57, 0.2);
}

.goalPlanTimelineRow.event .goalPlanTimelineBar {
  background: rgba(59, 130, 246, 0.7);
  box-shadow: 0 6px 14px rgba(59, 130, 246, 0.2);
}

.goalPlanTimelineMeta {
  flex: 0 0 auto;
  padding: 2px 6px;
  border-radius: 999px;
  font-size: 9px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  background: rgba(17, 24, 39, 0.08);
  color: var(--muted);
}

.goalPlanTimelineRow.event .goalPlanTimelineMeta {
  background: rgba(59, 130, 246, 0.15);
  color: #2563EB;
}

.goalPlanTimelineRow.empty .goalPlanTimelineTrack {
  background: none;
  border: 1px dashed rgba(17, 24, 39, 0.16);
}

.goalPlanTimelinePlaceholder {
  height: 100%;
  width: 100%;
  opacity: 0.4;
}

.goalPlanPreviewMaximized .goalPlanTimeline {
  gap: var(--plan-row-gap);
}

.goalPlanPreviewMaximized .goalPlanTimelineTrack {
  height: var(--plan-track-h);
}

.goalPlanPreviewMaximized .goalPlanTimelineRow.empty .goalPlanTimelineTrack {
  height: calc(var(--plan-track-h) - 2px);
}

.goalPlanTimelineScaleRow {
  display: grid;
  grid-template-columns: minmax(var(--plan-label-min), var(--plan-label-max)) minmax(0, 1fr);
  gap: var(--plan-col-gap);
  align-items: end;
  position: sticky;
  top: 0;
  z-index: 2;
  background: var(--panel);
  padding-bottom: 2px;
}

.goalPlanTimelineScaleLabel {
  font-size: var(--plan-date-size);
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--muted);
}

.goalPlanTimelineScale {
  display: grid;
  grid-template-columns: repeat(var(--goal-plan-grid, 6), minmax(0, 1fr));
  gap: 0;
}

.goalPlanTimelineScaleCell {
  padding: 2px 4px;
  border-right: 1px solid rgba(17, 24, 39, 0.08);
  font-size: var(--plan-date-size);
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--muted);
  background: rgba(17, 24, 39, 0.03);
}

.goalPlanTimelineScaleCell:last-child {
  border-right: none;
}

.goalPlanPreviewMaximized .goalPlanTimelineBar {
  top: 1px;
  bottom: 1px;
}

.goalWeeklyGrid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
  gap: 12px;
}

.goalWeeklyCard {
  border-radius: 18px;
  border: 1px solid var(--border);
  background: var(--panel);
  padding: 14px;
  display: grid;
  gap: 6px;
}

.goalWeeklyLabel {
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalWeeklyValue {
  font-size: 18px;
  font-weight: 900;
  color: var(--text);
}

.goalWeeklyMeta {
  font-size: 11px;
  font-weight: 800;
  color: var(--muted);
}

.goalActivity {
  display: grid;
  gap: 10px;
}

.goalActivityItem {
  text-align: left;
  border-radius: 16px;
  border: 1px solid var(--border);
  background: var(--panel);
  padding: 12px;
  transition: border-color 120ms ease, box-shadow 120ms ease, transform 120ms ease;
}

.goalActivityItem:hover {
  border-color: var(--border2);
  box-shadow: 0 14px 22px rgba(15, 23, 42, 0.08);
  transform: translateY(-1px);
}

.goalActivityTop {
  display: flex;
  align-items: baseline;
  justify-content: space-between;
  gap: 12px;
}

.goalActivityTitle {
  font-size: 13px;
  font-weight: 800;
  color: var(--text);
}

.goalActivityMeta {
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--muted);
}

.goalActivitySub {
  margin-top: 6px;
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--muted);
}

.goalActivityTable {
  display: grid;
  gap: 6px;
  overflow-x: auto;
}

.goalActivityRow {
  display: grid;
  grid-template-columns: 140px 90px 120px minmax(0, 1fr) minmax(0, 1.2fr);
  align-items: center;
  gap: 10px;
  padding: 10px 12px;
  border-radius: 14px;
  border: 1px solid var(--border);
  background: var(--panel);
  font-size: 11px;
  font-weight: 800;
  color: var(--text);
  text-align: left;
  cursor: pointer;
  min-width: 720px;
}

.goalActivityRow:hover {
  border-color: var(--border2);
  box-shadow: 0 12px 20px rgba(15, 23, 42, 0.08);
}

.goalActivityHeader {
  background: transparent;
  border: none;
  padding: 0 4px 6px;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  font-size: 10px;
  color: var(--muted);
  cursor: default;
}

.goalActivityHeader:hover {
  box-shadow: none;
}

.goalActivityTitleCell {
  font-size: 12px;
  font-weight: 900;
}

.goalActivityNotes {
  color: var(--muted);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.goalProjects {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.goalProjectTag {
  padding: 6px 10px;
  border-radius: 999px;
  border: 1px solid var(--border);
  background: var(--panel);
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--text);
}

.goalEmpty {
  padding: 12px 0;
  font-size: 11px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.12em;
  color: var(--muted);
}

.goalListItem {
  text-align: left;
  padding: 14px 16px;
  border-radius: 18px;
  border: 1px solid transparent;
  background: rgba(255, 255, 255, 0.6);
