}

.goalListItem:hover {
  border-color: rgba(17, 24, 39, 0.08);
  background: rgba(255, 255, 255, 0.8);
  box-shadow: 0 16px 26px rgba(15, 23, 42, 0.08);
  transform: translateY(-1px);
}

.goalListItem.active {
  border-color: rgba(217, 93, 57, 0.35);
