  flex-shrink: 0;
  position: sticky;
  top: 24px;
  align-self: flex-start;
}

.habitSidebar {
  width: min(600px, 100%);
  flex-shrink: 0;
  position: sticky;
  top: 24px;
  align-self: flex-start;
}

