  position: absolute;
  inset: -10%;
  border-radius: inherit;
  border: 1px solid var(--accentBorder);
  opacity: 0;
  transform: scale(0.85);
  pointer-events: none;
}

.modalMic.magicRecord.active::after {
  animation: magicPulse 1.6s ease-out infinite;
}

@keyframes magicPulse {
  0% {
    opacity: 0.65;
    transform: scale(0.85);
  }
  70% {
    opacity: 0;
    transform: scale(1.25);
  }
  100% {
    opacity: 0;
    transform: scale(1.25);
  }
}

