.kbTitleRow {
  display: flex;
  align-items: baseline;
  gap: 10px;
  min-width: 0;
}

.kbTitle {
  font-weight: 900;
}

.kbResults {
  font-size: 12px;
  color: var(--muted);
  font-weight: 700;
}

.kbToolBtn {
  height: 30px;
  border-radius: 999px;
  padding: 0 10px;
  background: transparent;
  border: 1px solid transparent;
  color: var(--muted);
  font-weight: 800;
}

.kbToolBtn:hover {
  background: rgba(17, 24, 39, 0.04);
  border-color: rgba(17, 24, 39, 0.08);
  color: var(--text);
}

.kbNew {
  height: 34px;
  border-radius: 10px;
  padding: 0 12px;
  font-weight: 800;
  background: var(--accentSoft);
  border-color: var(--accentBorder);
}

.kbGrid {
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 12px;
}

.kbCol {
  border: 1px solid var(--border);
  border-radius: 12px;
  background: var(--panel2);
  min-height: 520px;
  display: flex;
  flex-direction: column;
}

.kbColHeader {
  padding: 10px 10px 8px;
  border-bottom: 1px solid var(--border);
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 10px;
}

.kbColTitle {
  font-size: 12px;
  font-weight: 900;
  text-transform: uppercase;
  color: var(--muted);
}

.kbCount {
  font-size: 12px;
  color: var(--muted);
}

.kbColBody {
  padding: 10px;
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.kbCard {
  border: 1px solid var(--border);
  background: var(--panel);
  border-radius: 12px;
  padding: 10px;
  cursor: pointer;
  box-shadow: 0 6px 14px rgba(17, 24, 39, 0.04);
}

.kbCardTitle {
  font-weight: 800;
}

.kbCardMeta {
  margin-top: 6px;
  font-size: 12px;
  color: var(--muted);
  display: flex;
  gap: 8px;
  align-items: center;
}

.kbTag {
  padding: 2px 8px;
  border-radius: 999px;
  border: 1px solid var(--border);
  background: var(--panel2);
}

.kbTag.muted {
  opacity: 0.6;
}

.kbDot {
  opacity: 0.6;
}

/* Tasks table */
.taskTable {
  --task-grid: 48px minmax(220px, 2fr) minmax(140px, 1fr) 110px 110px 110px 140px 140px minmax(160px, 1fr) 90px 110px 110px;
  border-radius: 22px;
  border: 1px solid var(--border);
  background: var(--panel);
  box-shadow: var(--shadow2);
  overflow: hidden;
}

.taskTableHeader {
  display: grid;
  grid-template-columns: var(--task-grid);
  gap: 8px;
  align-items: center;
  padding: 12px 16px;
  background: rgba(255, 255, 255, 0.6);
  border-bottom: 1px solid var(--border);
  font-size: 11px;
  font-weight: 900;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  color: var(--muted);
}

.taskTableBody {
  display: flex;
  flex-direction: column;
}

.taskTableEmpty {
  padding: 48px 16px;
  text-align: center;
  font-size: 12px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  color: var(--muted);
}

.taskRow {
  display: grid;
  grid-template-columns: var(--task-grid);
  gap: 8px;
  align-items: center;
  padding: 12px 16px;
  border-bottom: 1px solid rgba(15, 23, 42, 0.06);
  transition: background 120ms ease, transform 120ms ease;
  cursor: pointer;
}

.taskRow:hover {
  background: var(--accentSoft);
}

.taskRow.active {
  background: var(--accentSoft);
}

.taskCol {
  min-width: 0;
  font-size: 12px;
  color: var(--text);
}

.taskCol.check {
  display: flex;
  align-items: center;
  justify-content: center;
}

.taskCheck {
  width: 26px;
  height: 26px;
  border-radius: 10px;
  border: 2px solid rgba(15, 23, 42, 0.2);
  display: grid;
  place-items: center;
  transition: all 120ms ease;
  background: transparent;
}

.taskCheck.active {
  border-color: #3d8856;
  background: #3d8856;
  box-shadow: 0 8px 16px rgba(61, 136, 86, 0.3);
}

.taskTitleRow {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.taskTitle {
  font-weight: 900;
  font-size: 13px;
  line-height: 1.2;
}

.taskTitle.done {
  text-decoration: line-through;
  color: var(--muted);
}

.taskSnippet {
  font-size: 11px;
  color: var(--muted);
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
}

.taskTagRow {
  display: flex;
  flex-wrap: wrap;
  gap: 6px;
}

.taskTag {
  padding: 3px 8px;
  border-radius: 999px;
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.06em;
  background: var(--accentSoft);
  color: #6d28d9;
}

.taskMuted {
  color: var(--muted);
}

.taskPill {
  padding: 4px 10px;
  border-radius: 999px;
  font-size: 10px;
  font-weight: 800;
  text-transform: uppercase;
  letter-spacing: 0.06em;
}

.taskPill.low {
  background: rgba(59, 130, 246, 0.12);
  color: #2563eb;
}

.taskPill.medium {
  background: rgba(245, 158, 11, 0.18);
  color: #b45309;
}

.taskPill.high {
  background: rgba(239, 68, 68, 0.18);
  color: #b91c1c;
}

.taskDate {
  font-weight: 800;
  font-size: 11px;
}

@media (max-width: 1200px) {
  .taskTable {
    overflow: auto;
  }
}

/* TickTick-inspired Tasks */
.ttRoot {
  height: 100%;
}

.ttShell {
  display: grid;
  grid-template-columns: 200px 1fr;
  height: 100%;
  min-height: 640px;
}

.ttLeft {
  border-right: 1px solid var(--border);
  background: var(--panel2);
  padding: 8px;
  display: flex;
  flex-direction: column;
  gap: 5px;
}

.ttLeftTitle {
  font-weight: 950;
  padding: 8px 8px 4px 8px;
  color: var(--text);
}

.ttNavItem {
  width: 100%;
  text-align: left;
  display: grid;
  grid-template-columns: 22px 1fr auto;
  gap: 8px;
  align-items: center;
  padding: 7px 8px;
  border-radius: 10px;
  background: transparent;
  border: 1px solid transparent;
  color: var(--text);
  font-weight: 900;
}

.ttNavItem:hover {
  background: rgba(17, 24, 39, 0.04);
  border-color: rgba(17, 24, 39, 0.06);
}

.ttNavItem.active {
  background: var(--accentSoft);
  border-color: var(--accentMid);
}

.ttNavIcon {
  color: var(--muted);
  display: grid;
  place-items: center;
}

.ttNavCount {
  color: var(--muted);
  font-weight: 900;
  font-size: 12px;
}

.ttMain {
  display: flex;
  flex-direction: column;
  padding: 10px;
  gap: 10px;
}

.ttHeader {
  display: flex;
  gap: 12px;
  justify-content: space-between;
  flex-wrap: wrap;
  align-items: flex-start;
}

.ttHeaderLeft {
  min-width: 280px;
}

.ttTitleRow {
  display: flex;
  align-items: baseline;
  gap: 10px;
}

.ttTitle {
  font-weight: 950;
}

.ttResults {
  font-size: 12px;
  color: var(--muted);
  font-weight: 800;
}

.ttSearchRow {
  margin-top: 8px;
}

.ttSearch {
  width: min(520px, 100%);
  height: 30px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel);
  padding: 0 12px;
}

.ttHeaderRight {
  display: flex;
  align-items: center;
  gap: 10px;
}

.ttQuickAdd {
  display: flex;
  gap: 8px;
  align-items: center;
  border: 1px solid var(--border);
  background: var(--panel);
  border-radius: 10px;
  padding: 4px;
}

.ttQuickInput {
  width: min(420px, 42vw);
  height: 28px;
  border: none;
  background: transparent;
  padding: 0 8px;
  outline: none;
}

.ttQuickBtn {
  height: 28px;
  border-radius: 9px;
  padding: 0 12px;
  font-weight: 950;
  background: var(--accentSoft);
  border: 1px solid var(--accentMid);
  color: var(--text);
}

.ttList {
  border: 1px solid var(--border);
  border-radius: 12px;
  overflow: hidden;
  background: var(--panel);
}

.ttEmpty {
  padding: 16px;
  color: var(--muted);
  font-weight: 800;
}

.ttRow {
  display: grid;
  grid-template-columns: 30px 1fr auto;
  gap: 10px;
  align-items: center;
  padding: 8px 10px;
  border-bottom: 1px solid var(--border);
  cursor: pointer;
}

.ttRow:last-child {
  border-bottom: none;
}

.ttRow:hover {
  background: rgba(17, 24, 39, 0.03);
}

.ttRow.selected {
  background: var(--accentSoft);
}

.ttRow.done .ttRowTitle {
  text-decoration: line-through;
  color: var(--muted);
}

.ttCheck {
  width: 22px;
  height: 22px;
  border-radius: 8px;
  border: 1px solid rgba(17, 24, 39, 0.18);
  background: rgba(255, 255, 255, 0.75);
  display: grid;
  place-items: center;
}

.ttCheckBox {
  width: 10px;
  height: 10px;
  border-radius: 3px;
  border: 1px solid rgba(17, 24, 39, 0.25);
}

.ttCheck.checked {
  border-color: var(--accentMid);
  background: var(--accentSoft);
}

.ttCheck.checked .ttCheckBox {
  background: var(--accent);
  border-color: var(--accent);
}

.ttRowMain {
  min-width: 0;
}

.ttRowTitle {
  font-weight: 950;
  font-size: 14px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.ttRowMeta {
  margin-top: 4px;
  display: flex;
  gap: 8px;
  align-items: center;
  flex-wrap: wrap;
}

.ttDue {
  font-size: 12px;
  font-weight: 900;
  color: rgba(17, 24, 39, 0.55);
}

.ttDue.muted {
  color: var(--muted);
}

.ttTag {
  height: 20px;
  border-radius: 999px;
  padding: 0 8px;
  font-size: 11px;
  font-weight: 900;
  background: var(--panel2);
  color: var(--muted);
  border: 1px solid var(--border);
}

.ttRowRight {
  font-size: 12px;
  font-weight: 900;
  color: var(--muted);
}

/* Agenda */
.agRoot {
  padding: 6px;
  display: flex;
  flex-direction: column;
  height: 100%;
  flex: 1;
  min-height: 0;
  overflow: hidden;
  --ag-slot-h: 88px;
  --ag-grid-line: rgba(15, 23, 42, 0.08);
  --ag-header-bg: var(--panel2);
  --ag-today-bg: rgba(17, 24, 39, 0.04);
  --ag-time-bg: var(--panel2);
}

:root[data-theme='dark'] .agRoot {
  --ag-grid-line: rgba(148, 163, 184, 0.06);
  --ag-header-bg: rgba(15, 19, 32, 0.92);
  --ag-today-bg: rgba(255, 255, 255, 0.05);
  --ag-time-bg: rgba(15, 19, 32, 0.92);
}

.agRoot.agWeek {
  padding: 4px;
  --ag-slot-h: 80px;
  --ag-grid-line: rgba(15, 23, 42, 0.06);
  --ag-header-bg: var(--panel);
  --ag-today-bg: rgba(163, 184, 124, 0.12);
  --ag-time-bg: rgba(248, 250, 252, 0.8);
