}

.agRoot.agWeek .agEventMeta,
.agRoot.agWeek .agResults,
.agRoot.agWeek .agSubtitle {
  font-variant-numeric: tabular-nums;
}

.agRoot.agWeek .agAllDayPill {
  border-radius: 6px;
}

.agRoot.agWeek .agAllDayIcon {
