}

.agTimeCell.hour {
  color: var(--text);
}

.agDayCol {
  position: relative;
  background: var(--panel);
  border-right: 1px solid var(--ag-grid-line);
}

.agDayCol:last-child {
  border-right: none;
}

.agDayCol.today {
  background: linear-gradient(180deg, var(--ag-today-bg), var(--panel));
}

.agRow {
  height: var(--ag-slot-h);
  border-bottom: 1px solid var(--ag-grid-line);
}

.agRow.hour {
  border-bottom-color: var(--border2);
}

.agEvent {
  position: absolute;
  left: 6px;
  width: calc(100% - 12px);
  border-radius: 8px;
  border: 1px solid var(--accentBorder);
  background: var(--accentSoft);
  padding: 6px 8px;
  cursor: pointer;
  overflow: hidden;
  transition: transform 120ms ease, box-shadow 120ms ease, background 120ms ease, border-color 120ms ease;
  display: flex;
  flex-direction: column;
  gap: 2px;
  isolation: isolate;
}


.agSegments {
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  pointer-events: none;
  z-index: 1;
}

.agSeg {
  position: absolute;
  left: 6px;
  right: 6px;
  height: 0;
}

.agSegLine {
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  height: 1px;
  background: rgba(17, 24, 39, 0.18);
}

:root[data-theme='dark'] .agSegLine {
  background: rgba(148, 163, 184, 0.10);
}

.agSegLabel {
  position: absolute;
  top: -8px;
  left: 0;
  font-size: 10px;
  font-weight: 900;
  color: var(--muted);
  background: rgba(255, 255, 255, 0.72);
  border: 1px solid rgba(17, 24, 39, 0.10);
  border-radius: 999px;
  padding: 0 6px;
  height: 16px;
  display: inline-flex;
  align-items: center;
  max-width: 70%;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

:root[data-theme='dark'] .agSegLabel {
  background: rgba(11, 16, 32, 0.68);
  border-color: rgba(148, 163, 184, 0.08);
}

.agStripe {
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 3px;
  opacity: 0.9;
  z-index: 1;
}

.agEventIcon {
  width: 14px;
  height: 14px;
  border-radius: 6px;
  border: 1px solid var(--accentBorder);
  background: var(--accentSoft);
  display: grid;
  place-items: center;
  color: var(--text);
  flex: 0 0 auto;
}

.agEvent.done {
  opacity: 0.75;
  filter: grayscale(0.2);
}

.agEvent.done .agEventTitle {
  text-decoration: line-through;
}

.agEventTitleRow {
  display: flex;
  align-items: center;
  gap: 5px;
  position: relative;
  z-index: 1;
}

.agCheck {
  width: 14px;
  height: 14px;
  padding: 0;
  border-radius: 5px;
  border: 1px solid rgba(17, 24, 39, 0.18);
  background: rgba(255, 255, 255, 0.75);
  display: grid;
  place-items: center;
}

.agCheckBox {
  width: 8px;
  height: 8px;
  border-radius: 2px;
  border: 1px solid rgba(17, 24, 39, 0.25);
  background: transparent;
}

.agCheck.checked .agCheckBox {
  background: var(--accent);
  border-color: var(--accent);
}

.agResize {
  position: absolute;
  left: 0;
  right: 0;
  bottom: 0;
  height: 8px;
  cursor: ns-resize;
  background: linear-gradient(180deg, transparent, rgba(17, 24, 39, 0.08));
  opacity: 0.6;
}

.agLogMark {
  position: absolute;
  width: 18px;
  height: 18px;
  border-radius: 6px;
  border: 1px solid var(--accentBorder);
  background: var(--accentSoft);
  display: grid;
  place-items: center;
  cursor: pointer;
  z-index: 2;
  transition: transform 120ms ease, box-shadow 120ms ease;
}

.agDraft {
  position: absolute;
  left: 6px;
  width: calc(100% - 12px);
  border-radius: 10px;
  border: 1px dashed var(--accent);
  background: var(--accentSoft);
  pointer-events: none;
}

.agEvent:hover .agResize {
  opacity: 1;
}

.agEvent:hover {
  transform: translateY(-1px);
  box-shadow: 0 6px 14px rgba(15, 23, 42, 0.12);
}

.agAllDayPill:hover,
.agLogMark:hover {
  transform: translateY(-1px);
  box-shadow: 0 6px 12px rgba(15, 23, 42, 0.12);
}

.agEventTitle {
  font-weight: 800;
  font-size: 11px;
  white-space: normal;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  line-height: 1.2;
}

.agEventMeta {
  margin-top: 2px;
  font-size: 10px;
  color: var(--muted);
  white-space: normal;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  position: relative;
  z-index: 1;
}

/* Tiimo-inspired Day Timeline */
.tmRoot {
