  justify-content: space-between;
  gap: 12px;
  flex-wrap: wrap;
}

.tmAllDay {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  padding: 0 2px;
}

.tmAllDayPill {
  height: 30px;
  border-radius: 12px;
  border: 1px solid var(--accentBorder);
  background: var(--accentSoft);
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 0 10px;
  overflow: hidden;
  max-width: 260px;
}

.tmAllDayPill.active {
