}

.tmAllDayIcon {
  width: 20px;
  height: 20px;
  border-radius: 9px;
  border: 1px solid var(--accentBorder);
  background: rgba(17, 24, 39, 0.03);
  display: grid;
  place-items: center;
  flex: 0 0 auto;
}

.tmAllDayText {
  font-weight: 900;
  color: var(--text);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.tmLeft {
  display: flex;
  align-items: center;
  gap: 8px;
  flex-wrap: wrap;
}

.tmTitleRow {
  display: flex;
  align-items: baseline;
  gap: 10px;
  margin-left: 6px;
}

.tmTitle {
  font-weight: 900;
}

.tmSubtitle {
  font-size: 12px;
  color: var(--muted);
  font-weight: 800;
}

.tmNavBtn {
  height: var(--control-h-xs);
  border-radius: 999px;
  padding: var(--control-pad-sm);
  background: var(--panel2);
  font-weight: 900;
  color: var(--muted);
}

.tmRight {
  display: flex;
  align-items: center;
  gap: 10px;
  flex-wrap: wrap;
  justify-content: flex-end;
}

.tmZoom {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 6px 10px;
  border: 1px solid var(--border);
  background: var(--panel);
  border-radius: 999px;
}

.tmZoomLabel {
  font-size: 12px;
  color: var(--muted);
  font-weight: 900;
}

.tmZoomBtn {
  height: 26px;
  border-radius: 999px;
  padding: 0 10px;
  font-size: 11px;
  font-weight: 900;
  background: var(--panel2);
  border: 1px solid transparent;
  color: var(--muted);
}

.tmZoomBtn.active {
  background: var(--accentSoft);
  border-color: rgba(217, 93, 57, 0.35);
  color: var(--text);
}

.tmNew {
  height: var(--control-h-sm);
  border-radius: var(--radius-md);
  padding: var(--control-pad-md);
  font-weight: 900;
  background: var(--cta-gradient);
  border: 1px solid var(--cta-border);
  color: #fff;
}

.tmTimeline {
  border: 1px solid var(--border);
  border-radius: var(--radius-lg);
  overflow-y: auto;
  overflow-x: hidden;
  flex: 1;
  min-height: 0;
  height: 100%;
  overscroll-behavior: contain;
  --tm-log-col: clamp(120px, 12vw, 200px);
}

.tmTimelineInner {
  display: grid;
  grid-template-columns: 64px minmax(0, 1fr) var(--tm-log-col);
  min-width: 100%;
  min-height: calc(var(--tm-slot-rows, 96) * var(--tm-slot-h));
}

.tmTimeCol {
  background: var(--panel2);
  border-right: 1px solid var(--border);
}

.tmTimeCell {
  height: var(--tm-slot-h);
  padding: 8px 8px;
  font-size: 11px;
  color: var(--muted);
  border-bottom: 1px solid var(--border);
  font-weight: 900;
}

.tmTimeCell.hour {
  color: var(--text);
}

.tmGrid {
  position: relative;
  background: var(--panel);
}

.tmLogCol {
  position: relative;
  background: var(--panel2);
  border-left: 1px solid var(--border);
  /* lane centerline offset from the right edge */
  --tm-lane-x: 10px;
}

/* Drag rail - vertical guide that appears during tracker drag */
.tmLogRail {
  position: absolute;
  right: var(--tm-lane-x);
  transform: translateX(50%);
  width: 4px;
  top: 0;
  bottom: 0;
  background: var(--accentMid);
  border-radius: 2px;
  opacity: 0;
  transition: opacity 0.15s ease;
  z-index: 5;
  pointer-events: none;
}

.tmLogCol.dragging .tmLogRail {
  opacity: 0.5;
}

/* Ghost position indicator - shows snapped drop position */
.tmLogGhost {
  position: absolute;
  right: var(--tm-lane-x);
  transform: translate(50%, -50%);
  width: 16px;
  height: 16px;
  background: var(--accent);
  border-radius: 50%;
  pointer-events: none;
  z-index: 6;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
}

/* Fade the dragging pill */
.tmLogBubble.isDragging {
  opacity: 0.4;
}

.tmLogResize {
  position: absolute;
  left: -6px;
  top: 0;
  bottom: 0;
  width: 12px;
  cursor: col-resize;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 3;
}

.tmLogResizeGrip {
  width: 4px;
  height: 48px;
  border-radius: 999px;
  background: rgba(17, 24, 39, 0.18);
  box-shadow: 0 0 0 1px rgba(17, 24, 39, 0.08);
  transition: background 120ms ease, box-shadow 120ms ease;
}

.tmLogResize:hover .tmLogResizeGrip {
  background: rgba(217, 93, 57, 0.45);
  box-shadow: 0 0 0 1px rgba(217, 93, 57, 0.35);
}

:root[data-theme='dark'] .tmLogResizeGrip {
  background: rgba(148, 163, 184, 0.12);
  box-shadow: 0 0 0 1px rgba(148, 163, 184, 0.08);
}

:root[data-theme='dark'] .tmLogResize:hover .tmLogResizeGrip {
  background: rgba(217, 93, 57, 0.55);
  box-shadow: 0 0 0 1px rgba(217, 93, 57, 0.4);
}

.tmLogLaneLine {
  position: absolute;
  right: calc(var(--tm-lane-x) - 1px);
  top: 0;
  bottom: 0;
  width: 2px;
  border-radius: 999px;
  background: var(--accentBorder);
  transform: none;
  z-index: 1;
}

.tmLogBubble {
  position: absolute;
  left: 0;
  right: 0;
  height: 0;
  pointer-events: auto;
  cursor: grab;
  --tm-pill-right: 30px;
  z-index: 2;
}

.tmLogBubble:active {
  cursor: grabbing;
}

.tmLogDot {
  position: absolute;
  right: var(--tm-lane-x);
  top: 0;
  transform: translate(50%, -50%);
  width: 12px;
  height: 12px;
  border-radius: 999px;
  border: 2px solid var(--accent);
  background: var(--accentMid);
  box-shadow: 0 4px 14px rgba(17, 24, 39, 0.10);
}

.tmLogConnector {
  position: absolute;
  right: var(--tm-lane-x);
  top: 0;
  transform: translateY(-50%);
  height: 2px;
  width: calc(var(--tm-pill-right) - var(--tm-lane-x));
  border-radius: 999px;
  background: var(--accentBorder);
}

.tmLogMark {
  position: absolute;
  right: var(--tm-lane-x);
  transform: translate(50%, -50%);
  width: 26px;
  height: 26px;
  padding: 0;
  border-radius: 999px;
  border: 1px solid var(--accentBorder);
  background: var(--accentSoft);
  color: var(--text);
  display: grid;
  place-items: center;
  cursor: pointer;
  pointer-events: auto;
  box-shadow: 0 10px 20px rgba(17, 24, 39, 0.08);
}

.tmLogMark:hover {
  filter: brightness(1.03);
}

.tmLogMark:active {
  transform: translate(50%, -50%) scale(0.98);
}

.tmRow {
  height: var(--tm-slot-h);
  border-bottom: 1px solid var(--border);
}

.tmRow.hour {
  border-bottom-color: var(--border2);
}

.tmLogPill {
  position: absolute;
  transform: translateY(-50%);
  right: var(--tm-pill-right);
  height: 30px;
  max-width: calc(100% - var(--tm-pill-right) - 12px);
  border-radius: 999px;
  padding: 0 12px;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  background: var(--accentSoft);
  border: 1px solid var(--accentMid);
  color: var(--text);
  cursor: pointer;
  overflow: hidden;
  pointer-events: auto;
}

.tmLogMenu {
  position: absolute;
  right: 70px;
  transform: translateY(-50%);
  padding: 10px 12px;
  border-radius: 12px;
  border: 1px solid rgba(17, 24, 39, 0.12);
  background: rgba(255, 255, 255, 0.95);
  box-shadow: 0 10px 24px rgba(17, 24, 39, 0.16);
  z-index: 5;
  width: 190px;
}

.tmLogMenuTitle {
  font-size: 12px;
  font-weight: 900;
  color: var(--text);
  margin-bottom: 8px;
}

.tmLogMenuRow {
  display: flex;
  align-items: center;
  gap: 8px;
}

.tmLogMenuValue {
  font-size: 12px;
  font-weight: 900;
  color: var(--text);
  width: 28px;
