}

/* Dashboard */
.dash {
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 14px;
}

/* Simple index pages (People/Places/Tags) */
.idxTop {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 10px;
  margin-bottom: 10px;
}

.idxSearch {
  flex: 1;
  height: 36px;
  border-radius: 12px;
  border: 1px solid var(--border);
  padding: 0 12px;
  background: var(--panel2);
  color: var(--text);
  font-weight: 800;
}

.idxInput {
  height: 32px;
  width: 76px;
  border-radius: 10px;
  border: 1px solid var(--border);
  background: var(--panel2);
  color: var(--text);
  font-weight: 900;
  padding: 0 8px;
  text-align: right;
}

.idxPill {
  height: 36px;
  border-radius: 999px;
  border: 1px solid var(--border);
  background: var(--panel2);
  padding: 0 12px;
