  align-items: center;
  gap: 8px;
  font-weight: 900;
  color: var(--muted);
  white-space: nowrap;
}

.idxGrid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 12px;
}

@media (max-width: 980px) {
  .idxGrid {
    grid-template-columns: 1fr;
  }
}

.idxList {
  border: 1px solid var(--border);
  border-radius: 14px;
  background: var(--panel);
