  color: var(--text);
}

:root[data-theme='dark'] .idxRow {
  border-bottom-color: rgba(148, 163, 184, 0.08);
}

.idxRow:last-child {
  border-bottom: none;
}

.idxRow.active {
  background: var(--accentSoft);
}

.idxRowMain {
  min-width: 0;
}

.idxRowTitle {
  font-weight: 950;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.idxRowMeta {
  margin-top: 2px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 850;
}

.idxRowRight {
  font-size: 12px;
  color: var(--muted);
  font-weight: 850;
  white-space: nowrap;
}

.idxDetail {
  border: 1px solid var(--border);
  border-radius: 14px;
  background: var(--panel);
  padding: 12px;
  overflow: hidden;
}

.idxDetailTitle {
  font-weight: 950;
  margin-bottom: 10px;
}

.idxDetailList {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.idxItem {
  width: 100%;
  text-align: left;
  border-radius: 14px;
  border: 1px solid rgba(17, 24, 39, 0.10);
  background: rgba(17, 24, 39, 0.03);
  padding: 10px 12px;
  cursor: pointer;
  color: var(--text);
}

:root[data-theme='dark'] .idxItem {
  border-color: rgba(148, 163, 184, 0.08);
  background: rgba(148, 163, 184, 0.06);
}

.idxHint {
  margin-top: 10px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 800;
}

.idxItemTop {
  display: grid;
  grid-template-columns: 1fr auto;
  gap: 10px;
  align-items: baseline;
}

.idxItemTitle {
  font-weight: 900;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.idxItemMeta {
  font-size: 12px;
  color: var(--muted);
  font-weight: 900;
  white-space: nowrap;
}

.idxItemSub {
  margin-top: 4px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 850;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

/* Rewards */
.rwGrid {
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 12px;
}

@media (max-width: 980px) {
  .rwGrid {
    grid-template-columns: 1fr;
  }
}

.rwCard {
  border: 1px solid var(--border);
  border-radius: 14px;
  background: var(--panel);
  padding: 12px;
}

.rwTitle {
  font-size: 12px;
  font-weight: 900;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  color: var(--muted);
}

.rwStatRow {
  margin-top: 12px;
  display: grid;
  grid-template-columns: repeat(4, minmax(0, 1fr));
  gap: 10px;
}

.rwStat {
  border-radius: 14px;
  border: 1px solid rgba(17, 24, 39, 0.10);
  background: rgba(17, 24, 39, 0.03);
  padding: 10px;
}

:root[data-theme='dark'] .rwStat {
  border-color: rgba(148, 163, 184, 0.08);
  background: rgba(148, 163, 184, 0.06);
}

.rwStatLabel {
  font-size: 11px;
  color: var(--muted);
  font-weight: 900;
  text-transform: uppercase;
  letter-spacing: 0.4px;
}

.rwStatValue {
  margin-top: 4px;
  font-weight: 950;
  font-size: 18px;
}

.rwHint {
  margin-top: 10px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 850;
}

.rwStore {
  margin-top: 12px;
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.rwItem {
  display: grid;
  grid-template-columns: 1fr auto;
  gap: 10px;
  align-items: center;
  border-radius: 14px;
  border: 1px solid rgba(17, 24, 39, 0.10);
  background: rgba(17, 24, 39, 0.03);
  padding: 10px 12px;
}

:root[data-theme='dark'] .rwItem {
  border-color: rgba(148, 163, 184, 0.08);
  background: rgba(148, 163, 184, 0.06);
}

.rwItemName {
  font-weight: 950;
}

.rwItemDesc {
  margin-top: 2px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 850;
}

.rwBuy {
  height: 34px;
  border-radius: 999px;
  padding: 0 12px;
  font-weight: 950;
  background: var(--accentSoft);
  border: 1px solid var(--accentMid);
  color: var(--text);
  cursor: pointer;
  white-space: nowrap;
}

.rwBuy.disabled {
  opacity: 0.45;
  cursor: default;
}

/* Habits */
.hbGrid {
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 12px;
}

@media (max-width: 980px) {
  .hbGrid {
    grid-template-columns: repeat(2, minmax(0, 1fr));
  }
}

@media (max-width: 640px) {
  .hbGrid {
    grid-template-columns: 1fr;
  }
}

.hbCard {
  text-align: left;
  border-radius: 16px;
  border: 1px solid rgba(17, 24, 39, 0.10);
  background: rgba(17, 24, 39, 0.03);
  padding: 12px;
  color: var(--text);
}

:root[data-theme='dark'] .hbCard {
  border-color: rgba(148, 163, 184, 0.08);
  background: rgba(148, 163, 184, 0.06);
}

.hbName {
  font-weight: 950;
}

.hbMeta {
  margin-top: 4px;
  font-size: 12px;
  color: var(--muted);
