  flex-direction: column;
  gap: 12px;
}

.hbDetailHead {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  gap: 12px;
}

.hbDetailTitle {
  font-weight: 950;
  font-size: 18px;
}

.hbDetailMeta {
  margin-top: 4px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 850;
}

.hbStats {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 10px;
}

.hbStat {
  border-radius: 12px;
  border: 1px solid var(--border);
  background: var(--panel2);
  padding: 10px 12px;
}

.hbStatLabel {
  font-size: 11px;
  color: var(--muted);
  text-transform: uppercase;
  letter-spacing: 0.08em;
  font-weight: 900;
}

.hbStatValue {
  margin-top: 6px;
  font-weight: 950;
  font-size: 18px;
}

.hbActions {
  display: flex;
  gap: 6px;
  margin-bottom: 8px;
}

.hbActionBtn {
  height: 26px;
  border-radius: 8px;
  padding: 0 10px;
  border: 1px solid var(--border);
  background: var(--panel2);
  font-weight: 900;
  font-size: 12px;
  color: var(--text);
  cursor: pointer;
}

.hbActionBtn.neg {
  border-color: rgba(248, 113, 113, 0.4);
  background: rgba(248, 113, 113, 0.12);
  color: #b91c1c;
}

:root[data-theme='dark'] .hbActionBtn.neg {
  color: #fca5a5;
}

.hbHeat {
  margin-top: 8px;
