  grid-template-columns: repeat(var(--hb-heat-cols), 8px);
  grid-template-rows: repeat(7, 8px);
  gap: 2px;
}

.hbHeatCell {
  width: 8px;
  height: 8px;
  border-radius: 2px;
  background: rgba(148, 163, 184, 0.08);
}

.dashHead {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  gap: 12px;
}

.dashHeadRight {
  display: flex;
  gap: 10px;
  align-items: center;
}

.dashTitle {
  font-weight: 950;
  letter-spacing: 0.02em;
}

/* Reports */
.reportFilters {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  flex-wrap: wrap;
  margin-bottom: 12px;
}

.reportToggles {
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
  font-size: 12px;
  color: var(--muted);
  font-weight: 800;
}

.reportToggle {
  display: inline-flex;
  align-items: center;
  gap: 6px;
}

.reportSummary {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
  gap: 12px;
  margin-bottom: 12px;
}

.reportSummaryItem {
  border-radius: 12px;
  border: 1px solid var(--border);
  background: var(--panel2);
  padding: 10px 12px;
}

.reportSummaryLabel {
  font-size: 11px;
  color: var(--muted);
  text-transform: uppercase;
  letter-spacing: 0.08em;
  font-weight: 900;
}

.reportSummaryValue {
  margin-top: 6px;
  font-weight: 950;
  font-size: 18px;
}

.reportGrid {
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 12px;
}

@media (max-width: 980px) {
  .reportGrid {
    grid-template-columns: 1fr;
  }
}

.reportCard {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.reportCardTitle {
  font-weight: 950;
}

.reportList {
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.reportRow {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  padding: 8px 10px;
  border-radius: 12px;
  border: 1px solid var(--border);
  background: var(--panel2);
}

.reportMain {
  min-width: 0;
}

.reportTitle {
  font-weight: 900;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.reportMeta {
  margin-top: 2px;
  font-size: 11px;
  color: var(--muted);
  font-weight: 800;
}

.reportMetrics {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  font-size: 11px;
  color: var(--muted);
  font-weight: 900;
}

.settingsTextarea {
  width: 100%;
  border-radius: 12px;
  border: 1px solid var(--border);
  padding: 10px 12px;
  background: var(--panel2);
  color: var(--text);
  font-family: inherit;
  font-size: 12px;
  line-height: 1.4;
  resize: vertical;
}

.dashSub {
  font-size: 12px;
  color: var(--muted);
  font-weight: 800;
  margin-top: 2px;
}

.dashRange {
  display: flex;
  gap: 6px;
  padding: 4px;
  border-radius: 999px;
  border: 1px solid var(--border);
  background: var(--panel2);
  box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.4);
}

.dashRangeBtn {
  height: 30px;
  border-radius: 999px;
  padding: 0 10px;
  border: none;
  background: transparent;
  color: var(--muted);
  font-weight: 900;
  cursor: pointer;
  transition: transform 120ms ease, box-shadow 120ms ease;
}

.dashRangeBtn.active {
  background: var(--accentMid);
  color: var(--text);
}

.dashGrid {
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 12px;
}

.dashWidget {
  position: relative;
}

.dashWidget.editing .dashCard {
  outline: 2px dashed var(--accentBorder);
  outline-offset: 2px;
}

.dashWidgetControls {
  position: absolute;
  top: 8px;
  right: 8px;
  display: flex;
  gap: 6px;
  z-index: 2;
}

.dashWidgetBtn {
  height: 24px;
  border-radius: 8px;
  padding: 0 8px;
  border: 1px solid var(--border);
  background: var(--panel2);
  color: var(--muted);
  font-weight: 900;
  cursor: pointer;
  font-size: 11px;
  transition: transform 120ms ease, box-shadow 120ms ease;
}

.dashHidden {
  margin-top: 12px;
  border: 1px dashed var(--border);
  border-radius: 12px;
  padding: 10px 12px;
  background: var(--panel2);
}

.dashHiddenTitle {
  font-size: 12px;
  font-weight: 900;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  color: var(--muted);
  margin-bottom: 8px;
}

.dashHiddenList {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.dashHiddenBtn {
  height: 28px;
  border-radius: 999px;
  padding: 0 12px;
  border: 1px solid var(--border);
  background: var(--panel);
  font-weight: 900;
  font-size: 12px;
  color: var(--text);
  transition: transform 120ms ease, box-shadow 120ms ease;
}

@media (max-width: 980px) {
  .dashGrid {
    grid-template-columns: 1fr;
  }
}

.dashCard {
  border: 1px solid var(--border);
  border-radius: 16px;
  background: linear-gradient(180deg, var(--panel), rgba(255, 255, 255, 0.9));
  padding: 12px;
  overflow: hidden;
  box-shadow: 0 18px 32px rgba(15, 23, 42, 0.08);
  transition: transform 140ms ease, box-shadow 140ms ease;
}

.dashCardTitle {
  font-size: 12px;
  font-weight: 900;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  color: var(--muted);
  margin-bottom: 8px;
}

.dashRow {
  display: grid;
  grid-template-columns: 240px 1fr;
  gap: 12px;
  align-items: center;
}

@media (max-width: 980px) {
  .dashRow {
    grid-template-columns: 1fr;
  }
}

.dashLegend {
  display: flex;
  flex-direction: column;
  gap: 6px;
  padding-right: 4px;
}

.dashLegend.compact {
  margin-top: 8px;
}

.dashLegendRow {
  display: flex;
  justify-content: space-between;
  gap: 10px;
  font-size: 12px;
  font-weight: 850;
  color: var(--text);
}

.dashLegendKey {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  color: var(--text);
}

.dashLegendVal {
  color: var(--muted);
  font-weight: 900;
  white-space: nowrap;
}

.dashPie {
  width: 220px;
  height: 220px;
  display: block;
}

.dashPieTotal {
  font-size: 22px;
  font-weight: 950;
  fill: rgba(17, 24, 39, 0.92);
}

.dashPieSub {
  font-size: 12px;
  font-weight: 900;
  fill: rgba(107, 114, 128, 0.95);
}

.dashCard:hover {
  transform: translateY(-1px);
  box-shadow: 0 22px 36px rgba(15, 23, 42, 0.12);
}

.dashWidgetBtn:hover,
.dashHiddenBtn:hover,
.dashRangeBtn:hover {
  transform: translateY(-1px);
  box-shadow: 0 10px 18px rgba(15, 23, 42, 0.12);
}

:root[data-theme='dark'] .dashCard {
  background: linear-gradient(180deg, rgba(11, 16, 32, 0.98), rgba(11, 16, 32, 0.9));
  box-shadow: 0 18px 32px rgba(0, 0, 0, 0.3);
}

:root[data-theme='dark'] .dashPieTotal {
  fill: rgba(226, 232, 240, 0.9);
}

:root[data-theme='dark'] .dashPieSub {
  fill: rgba(148, 163, 184, 0.8);
}

.dashSmallNote {
  margin-top: 6px;
  font-size: 12px;
  color: var(--muted);
  font-weight: 800;
}

.dashRadarWrap {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.dashRadar {
  width: 260px;
  height: 260px;
  display: block;
  margin: 0 auto;
}

.dashRadarLabel {
  font-size: 11px;
  font-weight: 900;
  fill: rgba(107, 114, 128, 0.95);
}

.dashList {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.dashListRow {
  display: flex;
  justify-content: space-between;
  gap: 10px;
  font-size: 13px;
  font-weight: 850;
  border: 1px solid rgba(17, 24, 39, 0.10);
  background: rgba(17, 24, 39, 0.03);
  border-radius: 12px;
  padding: 8px 10px;
}

:root[data-theme='dark'] .dashListRow {
  border-color: rgba(148, 163, 184, 0.08);
  background: rgba(148, 163, 184, 0.06);
}

.dashListKey {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.dashListVal {
  color: var(--muted);
  font-weight: 900;
  white-space: nowrap;
}

.dashEmpty {
  font-size: 12px;
  color: var(--muted);
  font-weight: 900;
  padding: 6px 2px;
}

