  min-height: auto;
  padding: 18px;
  background: var(--panel);
}

.goalSection {
  background: var(--panel2);
  border: 1px solid var(--border);
  border-radius: 24px;
  padding: 18px;
  display: grid;
  gap: 16px;
}

.goalSectionPlan {
  padding: 22px;
  min-height: 420px;
}

.goalChipRow {
  display: flex;
  align-items: center;
  gap: 8px;
  flex-wrap: wrap;
}

.goalChip {
  padding: 6px 12px;
  border-radius: 999px;
  border: 1px solid var(--border);
  background: var(--panel);
  font-size: 11px;
  font-weight: 800;
  color: var(--muted);
