  border-color: rgba(217, 93, 57, 0.35);
  box-shadow: 0 10px 18px rgba(217, 93, 57, 0.12);
}

.goalChipMeta {
  font-size: 10px;
  font-weight: 800;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted);
}

.goalLimitNote {
  font-size: 10px;
  font-weight: 800;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted);
}

.goalSectionHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
}

.goalSectionEyebrow {
