  height: 26px;
  border-radius: 8px;
  border: 1px solid rgba(17, 24, 39, 0.18);
  background: rgba(255, 255, 255, 0.75);
  color: rgba(17, 24, 39, 0.55);
  display: grid;
  place-items: center;
}

.goalCheck.checked {
  border-color: rgba(16, 185, 129, 0.4);
  background: rgba(16, 185, 129, 0.16);
  color: #166534;
}

.goalCheckLink {
  width: 24px;
  height: 24px;
  border-radius: 8px;
  border: 1px solid rgba(17, 24, 39, 0.14);
  background: rgba(255, 255, 255, 0.75);
  color: var(--muted);
  display: grid;
  place-items: center;
  transition: border-color 120ms ease, color 120ms ease;
}

.goalCheckLink:hover {
  color: var(--text);
  border-color: rgba(17, 24, 39, 0.24);
}

.goalCheckMenu {
  width: 24px;
  height: 24px;
  border-radius: 8px;
  border: 1px solid rgba(17, 24, 39, 0.14);
  background: rgba(255, 255, 255, 0.75);
  color: var(--muted);
  display: grid;
  place-items: center;
  transition: border-color 120ms ease, color 120ms ease;
}

.goalCheckMenu:hover {
  color: var(--text);
  border-color: rgba(17, 24, 39, 0.24);
