}

/* Editable title - contenteditable or input */
.obsTitleEditable {
  border: none;
  background: transparent;
  outline: none;
  width: 100%;
  cursor: text;
  transition: background 150ms ease, padding 150ms ease;
}

.obsTitleEditable:hover {
  background: rgba(0, 0, 0, 0.02);
  border-radius: 4px;
}

.obsTitleEditable:focus {
  background: rgba(26, 115, 232, 0.04);
  border-radius: 4px;
  padding: 4px 8px;
  margin: -4px -8px;
}

