  color: var(--muted);
  opacity: 0.6;
  font-style: normal;
}

/* Title with subtle underline accent */
.obsTitleAccent {
  position: relative;
  display: inline-block;
}

.obsTitleAccent::after {
  content: '';
  position: absolute;
  left: 0;
  bottom: -4px;
  width: 48px;
  height: 4px;
  background: var(--accent);
  border-radius: 2px;
  opacity: 0.8;
}

/* Title row container - for title + actions */
.obsTitleRow {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  gap: 16px;
  margin-bottom: 8px;
}

.obsTitleRow .obsTitle {
  flex: 1;
  min-width: 0;
}

/* Title group - title + subtitle stacked */
.obsTitleGroup {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

/* Subtitle styles */
.obsTitleSubtitle {
  font-family: 'Google Sans', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  font-size: 14px;
  font-weight: 400;
  color: var(--muted);
  line-height: 1.5;
  margin: 0;
}

.obsTitleSubtitleSm {
  font-family: 'Google Sans', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  font-size: 12px;
  font-weight: 400;
  color: var(--muted);
  line-height: 1.4;
  margin: 0;
}

/* Title meta info - date, author, etc. */
.obsTitleMeta {
  font-size: 12px;
  font-weight: 400;
  color: var(--muted);
  display: flex;
  align-items: center;
  gap: 12px;
  margin-top: 8px;
}

.obsTitleMeta span {
  display: inline-flex;
  align-items: center;
  gap: 4px;
}

/* Title with icon prefix */
.obsTitleWithIcon {
  display: flex;
  align-items: center;
  gap: 12px;
}

.obsTitleWithIcon .obsTitleIcon {
  flex-shrink: 0;
  width: 32px;
  height: 32px;
  display: grid;
  place-items: center;
  color: var(--accent);
  opacity: 0.9;
}

.obsTitleWithIcon.obsTitleSm .obsTitleIcon {
  width: 24px;
  height: 24px;
}

/* Bold weight variant */
.obsTitle.bold,
.obsTitleLg.bold,
.obsTitleMd.bold,
.obsTitleSm.bold {
  font-weight: 700;
}

/* Light weight variant */
.obsTitle.light,
.obsTitleLg.light,
.obsTitleMd.light {
  font-weight: 300;
}

/* Muted color variant */
.obsTitle.muted,
.obsTitleLg.muted,
.obsTitleMd.muted,
.obsTitleSm.muted {
  color: var(--muted);
}

/* Truncate long titles */
.obsTitleTruncate {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

/* Multi-line clamp */
.obsTitleClamp {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.obsTitleClamp3 {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

/* Dark theme adjustments */
:root[data-theme='dark'] .obsTitleEditable:hover {
  background: rgba(255, 255, 255, 0.03);
}

:root[data-theme='dark'] .obsTitleEditable:focus {
  background: rgba(138, 180, 248, 0.08);
}

:root[data-theme='dark'] .obsTitleAccent::after {
  opacity: 0.7;
}
