   164→type ThemeContextValue = {
   165→  themeMode: ThemeMode;
   166→  displayMode: DisplayMode;
   167→  resolvedTheme: ResolvedTheme;
   168→  palette: typeof ThemePalettes.dark;
   169→  sizes: typeof DisplaySizes.big;
   170→  isDark: boolean;
   171→  setThemeMode: (mode: ThemeMode) => void;
   172→  setDisplayMode: (mode: DisplayMode) => void;
   173→};
   174→
   175→const ThemeContext = createContext<ThemeContextValue | null>(null);
   176→
   177→const THEME_STORAGE_KEY = '@insight/themeMode';
   178→const DISPLAY_STORAGE_KEY = '@insight/displayMode';
   179→
   180→type ThemeProviderProps = {
   181→  children: ReactNode;
   182→};
   183→
   184→export function ThemeProvider({ children }: ThemeProviderProps) {
   185→  const systemScheme = useSystemColorScheme();
   186→  const [themeMode, setThemeModeState] = useState<ThemeMode>('system');
   187→  const [displayMode, setDisplayModeState] = useState<DisplayMode>('big');
   188→  const [isLoaded, setIsLoaded] = useState(false);
   189→
   190→  // Load persisted preferences on mount
   191→  useEffect(() => {
   192→    AsyncStorage.multiGet([THEME_STORAGE_KEY, DISPLAY_STORAGE_KEY])
   193→      .then(([themeResult, displayResult]) => {
   194→        if (themeResult[1]) {
   195→          setThemeModeState(themeResult[1] as ThemeMode);
   196→        }
   197→        if (displayResult[1]) {
   198→          setDisplayModeState(displayResult[1] as DisplayMode);
   199→        }
   200→        setIsLoaded(true);
   201→      })
   202→      .catch((error) => {
   203→        console.warn('Failed to load theme preferences:', error);
   204→        setIsLoaded(true);
   205→      });
   206→  }, []);
   207→
   208→  // Persist theme mode to AsyncStorage
   209→  const setThemeMode = (mode: ThemeMode) => {
   210→    setThemeModeState(mode);
   211→    AsyncStorage.setItem(THEME_STORAGE_KEY, mode).catch((error) => {
   212→      console.warn('Failed to save theme mode:', error);
   213→    });
   214→  };
   215→
   216→  // Persist display mode to AsyncStorage
   217→  const setDisplayMode = (mode: DisplayMode) => {
   218→    setDisplayModeState(mode);
   219→    AsyncStorage.setItem(DISPLAY_STORAGE_KEY, mode).catch((error) => {
   220→      console.warn('Failed to save display mode:', error);
   221→    });
   222→  };
   223→
   224→  // Resolve 'system' to actual theme
   225→  const resolvedTheme: ResolvedTheme = useMemo(() => {
   226→    if (themeMode === 'system') {
   227→      // System dark → dark, system light → warm (the "middle" mode)
   228→      return systemScheme === 'dark' ? 'dark' : 'warm';
   229→    }
   230→    return themeMode;
   231→  }, [themeMode, systemScheme]);
   232→
   233→  // Get current palette and sizes
   234→  const palette = ThemePalettes[resolvedTheme];
   235→  const sizes = DisplaySizes[displayMode];
   236→  const isDark = resolvedTheme === 'dark' || resolvedTheme === 'olive' || resolvedTheme === 'oliveOrange' || resolvedTheme === 'roseGold';
   237→
   238→  const value = useMemo<ThemeContextValue>(
   239→    () => ({
   240→      themeMode,
   241→      displayMode,
   242→      resolvedTheme,
   243→      palette,
   244→      sizes,
   245→      isDark,
   246→      setThemeMode,
   247→      setDisplayMode,
   248→    }),
   249→    [themeMode, displayMode, resolvedTheme, palette, sizes, isDark]
   250→  );
   251→
   252→  // Don't render until preferences are loaded to avoid flash
   253→  if (!isLoaded) {
   254→    return null;
   255→  }
   256→
   257→  return <ThemeContext.Provider value={value}>{children}</ThemeContext.Provider>;
   258→}
   259→
   260→export function useTheme(): ThemeContextValue {
   261→  const context = useContext(ThemeContext);
   262→  if (!context) {
   263→    throw new Error('useTheme must be used within a ThemeProvider');
   264→  }
   265→  return context;
   266→}
   267→
   268→// Helper hook for checking if current theme is dark-based
   269→export function useIsDark(): boolean {
   270→  const { isDark } = useTheme();
   271→  return isDark;
   272→}
   273→
   274→// Helper to get theme-aware colors (for components that can't use hooks)
   275→export function getThemePalette(theme: ResolvedTheme) {
   276→  return ThemePalettes[theme];
   277→}
