     1→export type ThemePreference = 'system' | 'light' | 'dark' | 'warm' | 'olive' | 'oliveOrange' | 'roseGold'
     2→export type ResolvedTheme = 'light' | 'dark' | 'warm' | 'olive' | 'oliveOrange' | 'roseGold'
     3→
     4→const STORAGE_KEY = 'insight5.ui.theme.v2'
     5→export const THEME_CHANGED_EVENT = 'insight5.theme.changed'
     6→
     7→const VALID_THEMES: ThemePreference[] = ['system', 'light', 'dark', 'warm', 'olive', 'oliveOrange', 'roseGold']
     8→const DARK_THEMES: ResolvedTheme[] = ['dark', 'olive', 'oliveOrange', 'roseGold']
