    10→export function getSystemTheme(): ResolvedTheme {
    11→  try {
    12→    // System dark → 'dark', System light → 'warm' (the default warm sand look)
    13→    return window.matchMedia?.('(prefers-color-scheme: dark)')?.matches ? 'dark' : 'warm'
    14→  } catch {
    15→    return 'warm'
    16→  }
    17→}
    18→
    19→export function resolveTheme(pref: ThemePreference): ResolvedTheme {
    20→  if (pref === 'system') return getSystemTheme()
    21→  return pref as ResolvedTheme
    22→}
    23→
    24→export function isDarkTheme(theme: ResolvedTheme): boolean {
    25→  return DARK_THEMES.includes(theme)
    26→}
    27→
    28→export function loadThemePreference(): ThemePreference {
    29→  try {
    30→    const raw = localStorage.getItem(STORAGE_KEY)
    31→    if (raw && VALID_THEMES.includes(raw as ThemePreference)) {
    32→      return raw as ThemePreference
    33→    }
    34→    // Migration from v1
    35→    const oldKey = 'insight5.ui.theme.v1'
    36→    const old = localStorage.getItem(oldKey)
    37→    if (old === 'light') return 'light'
    38→    if (old === 'dark') return 'dark'
    39→  } catch {
    40→    // ignore
    41→  }
    42→  return 'system'
    43→}
    44→
    45→export function saveThemePreference(pref: ThemePreference) {
    46→  try {
    47→    localStorage.setItem(STORAGE_KEY, pref)
    48→  } catch {
    49→    // ignore
    50→  }
    51→}
    52→
    53→export function applyTheme(theme: ResolvedTheme) {
    54→  document.documentElement.dataset.theme = theme
    55→}
    56→
    57→export function initTheme() {
    58→  const pref = loadThemePreference()
    59→  const resolved = resolveTheme(pref)
    60→  applyTheme(resolved)
    61→  return { pref, resolved }
    62→}
